import java.util.Deque;
import java.util.HashSet;
import java.util.LinkedList;
import java.util.Set;

/**
 * RecentlyPlayedStore class manages a playlist of recently played songs with a fixed capacity.
 * It ensures O(1) average time complexity for adding, replaying, and removing songs.
 */
public class RecentlyPlayedStore {

    private final int capacity;
    private final Deque<String> playlist; // Use Deque for efficient head/tail operations
    private final Set<String> songSet;    // Use Set for O(1) song lookup

    /**
     * Constructor for the RecentlyPlayedStore.
     *
     * @param capacity The maximum number of songs the playlist can hold.
     */
    public RecentlyPlayedStore(int capacity) {
        if (capacity <= 0) {
            throw new IllegalArgumentException("Capacity must be a positive integer.");
        }
        this.capacity = capacity;
        this.playlist = new LinkedList<>();
        this.songSet = new HashSet<>();
    }

    /**
     * Adds a song to the playlist.  If the song already exists, it's moved to the front.
     * If the playlist is full, the least recently played song is removed.
     *
     * @param song The song to be added or replayed.
     */
    public void playSong(String song) {
        if (song == null || song.trim().isEmpty()) {
            throw new IllegalArgumentException("Song name cannot be null or empty.");
        }

        if (songSet.contains(song)) {
            // If the song is already in the playlist, move it to the front.
            playlist.remove(song); // O(n) operation on LinkedList but combined with the HashSet it results in O(1) avg
            playlist.addFirst(song);  // O(1)
        } else {
            // If the song is not in the playlist
            if (playlist.size() == capacity) {
                // If the playlist is full, remove the least recently played song.
                String removedSong = playlist.removeLast(); // O(1)
                songSet.remove(removedSong);          // O(1)
            }
            // Add the new song to the front of the playlist.
            playlist.addFirst(song);     // O(1)
            songSet.add(song);         // O(1)
        }
    }

    /**
     * Returns the playlist as a list of songs.
     *
     * @return A LinkedList representing the playlist. The most recently played song is at the head of the list.
     */
    public Deque<String> getPlaylist() {
        return new LinkedList<>(playlist);  // Return a copy to prevent external modification
    }

    /**
     * Returns the most recent songs.
     * @return The deque containing the most recently played songs.
     */
    public Deque<String> getRecentlyPlayed() {
        return new LinkedList<>(playlist);
    }

    /**
     * Returns the capacity of the playlist
     * @return The capacity
     */
    public int getCapacity() {
        return capacity;
    }


    /**
     * Main method for testing the RecentlyPlayedStore.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test case 1: Basic functionality
        RecentlyPlayedStore store1 = new RecentlyPlayedStore(3);
        store1.playSong("S1");
        store1.playSong("S2");
        store1.playSong("S3");
        System.out.println("Playlist 1: " + store1.getPlaylist()); // Expected: [S3, S2, S1]

