        store2.playSong("A"); //Play the same song again
        System.out.println("Playlist 2 after replaying A again: " + store2.getPlaylist()); //Expected: [A, B, E, D, C]

        // Test case 3: Playlist at capacity and adding new songs
        RecentlyPlayedStore store3 = new RecentlyPlayedStore(2);
