public class MaxSubarraySum {

    /**
     * Finds the contiguous subarray with the largest sum in a given array.
     * This implementation uses Kadane's Algorithm, which is an efficient
     * dynamic programming approach.
     *
     * @param arr The input array of integers.
     * @return The maximum sum of any contiguous subarray in the input array.
     *         Returns 0 if the input array is null or empty.
     */
    public static int maxSubarraySum(int[] arr) {
        // Handle edge cases: null or empty array
        if (arr == null || arr.length == 0) {
            return 0;
        }

        int maxSoFar = arr[0];  // Initialize with the first element as the maximum so far
        int currentMax = arr[0]; // Initialize with the first element as the current maximum

        // Iterate through the array starting from the second element
        for (int i = 1; i < arr.length; i++) {
            // Calculate the current maximum: either the current element itself
            // or the current element added to the previous current maximum.
            // This decides whether to start a new subarray from the current element
            // or extend the previous subarray.
            currentMax = Math.max(arr[i], currentMax + arr[i]);

            // Update the maximum so far if the current maximum is greater
            maxSoFar = Math.max(maxSoFar, currentMax);
        }

        return maxSoFar;
    }

    /**
     * Main method to test the maxSubarraySum function with various test cases.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test Case 1: Basic test case
        int[] arr1 = {-2, -3, 4, -1, -2, 1, 5, -3};
        System.out.println("Test Case 1: " + maxSubarraySum(arr1)); // Expected Output: 7

        // Test Case 2: All negative numbers
        int[] arr2 = {-1, -2, -3, -4, -5};
        System.out.println("Test Case 2: " + maxSubarraySum(arr2)); // Expected Output: -1

        // Test Case 3: All positive numbers
        int[] arr3 = {1, 2, 3, 4, 5};
        System.out.println("Test Case 3: " + maxSubarraySum(arr3)); // Expected Output: 15

        // Test Case 4: Mixed positive and negative numbers
        int[] arr4 = {-2, 1, -3, 4, -1, 2, 1, -5, 4};
        System.out.println("Test Case 4: " + maxSubarraySum(arr4)); // Expected Output: 6

        // Test Case 5: Single element array
        int[] arr5 = {5};
        System.out.println("Test Case 5: " + maxSubarraySum(arr5)); // Expected Output: 5

        // Test Case 6: Empty array
        int[] arr6 = {};
        System.out.println("Test Case 6: " + maxSubarraySum(arr6)); // Expected Output: 0

        // Test Case 7: Array with zero
        int[] arr7 = {-2, 0, -3, 4, -1, 2, 1, -5, 4};
        System.out.println("Test Case 7: " + maxSubarraySum(arr7)); // Expected Output: 6

        // Test Case 8: Array with only one positive number and other negative numbers
        int[] arr8 = {-10, -20, -3, 4, -100, -200, -30};
        System.out.println("Test Case 8: " + maxSubarraySum(arr8)); // Expected output: 4

        // Test Case 9: Array with a large positive subarray
        int[] arr9 = {-10000, 1000, 2000, 3000, 4000, 5000, -10000};
        System.out.println("Test Case 9: " + maxSubarraySum(arr9)); // Expected output: 15000
    }
}

