public class InversionCount {

    /**
     * Calculates the number of inversion pairs in an array of integers.
     * An inversion pair (i, j) is defined as arr[i] > arr[j] where 0 <= i < j < n.
     *
     * @param arr The input array of integers.
     * @return The total number of inversion pairs in the array.
     */
    public static long countInversions(int[] arr) {
        if (arr == null || arr.length <= 1) {
            return 0; // No inversions if the array is null or has only one element
        }

        int n = arr.length;
        int[] tempArray = new int[n]; // Auxiliary array for merging
        return mergeSortAndCount(arr, tempArray, 0, n - 1);
    }

    /**
     * Recursive helper method to perform merge sort and count inversions.
     *
     * @param arr       The input array.
     * @param tempArray Auxiliary array for merging.
     * @param left      The starting index of the subarray.
     * @param right     The ending index of the subarray.
     * @return The number of inversions in the subarray.
     */
    private static long mergeSortAndCount(int[] arr, int[] tempArray, int left, int right) {
        long inversions = 0;
        if (left < right) {
            int mid = (left + right) / 2;

            inversions += mergeSortAndCount(arr, tempArray, left, mid);       // Count inversions in the left subarray
            inversions += mergeSortAndCount(arr, tempArray, mid + 1, right);  // Count inversions in the right subarray

            inversions += merge(arr, tempArray, left, mid, right);            // Count inversions during the merge process
        }
        return inversions;
    }

    /**
     * Merges two sorted subarrays and counts inversions.
     *
     * @param arr       The input array.
     * @param tempArray Auxiliary array for merging.
     * @param left      The starting index of the left subarray.
     * @param mid       The ending index of the left subarray.
     * @param right     The ending index of the right subarray.
     * @return The number of inversions encountered during the merge.
     */
    private static long merge(int[] arr, int[] tempArray, int left, int mid, int right) {
        int i = left;      // Index for the left subarray
        int j = mid + 1;   // Index for the right subarray
        int k = left;      // Index for the merged subarray
        long inversions = 0;

        while (i <= mid && j <= right) {
            if (arr[i] <= arr[j]) {
                tempArray[k++] = arr[i++];
            } else {
                tempArray[k++] = arr[j++];
                inversions += (mid - i + 1); // If arr[i] > arr[j], then all elements from i to mid are greater than arr[j]
            }
        }

        // Copy remaining elements from the left subarray, if any
        while (i <= mid) {
            tempArray[k++] = arr[i++];
        }

        // Copy remaining elements from the right subarray, if any
        while (j <= right) {
            tempArray[k++] = arr[j++];
        }

        // Copy the merged subarray back to the original array
        System.arraycopy(tempArray, left, arr, left, right - left + 1);

        return inversions;
    }

    public static void main(String[] args) {
        // Test cases

        // Example 1
        int[] arr1 = {2, 4, 1, 3, 5};
        System.out.println("Example 1: Inversion count = " + countInversions(arr1)); // Expected Output: 3

        // Example 2
        int[] arr2 = {5, 4, 3, 2, 1};
        System.out.println("Example 2: Inversion count = " + countInversions(arr2)); // Expected Output: 10

        // Example 3: Empty array
        int[] arr3 = {};
        System.out.println("Example 3: Inversion count = " + countInversions(arr3)); // Expected Output: 0

        // Example 4: Array with one element
        int[] arr4 = {7};
        System.out.println("Example 4: Inversion count = " + countInversions(arr4)); // Expected Output: 0

        // Example 5: Array with duplicates
        int[] arr5 = {3, 1, 2, 1};
        System.out.println("Example 5: Inversion count = " + countInversions(arr5)); // Expected Output: 4

        // Example 6: Already sorted array
        int[] arr6 = {1, 2, 3, 4, 5};
        System.out.println("Example 6: Inversion count = " + countInversions(arr6)); // Expected Output: 0

        // Example 7: Array with negative numbers
        int[] arr7 = {-1, -2, -3};
        System.out.println("Example 7: Inversion count = " + countInversions(arr7)); // Expected Output: 3

        // Example 8: Array with mixed positive and negative numbers
        int[] arr8 = {2, -1, 3, -2};
        System.out.println("Example 8: Inversion count = " + countInversions(arr8)); // Expected Output: 4

        // Example 9: Large array (testing performance)
        int[] arr9 = new int[100000];
        for (int i = 0; i < 100000; i++) {
            arr9[i] = 100000 - i; // Reverse order to maximize inversions
        }
        System.out.println("Example 9: Inversion count = " + countInversions(arr9)); // Expected Output: A large number

        // Example 10: Array with zero
        int[] arr10 = {0, 0, 0, 0};
        System.out.println("Example 10: Inversion Count = " + countInversions(arr10)); // Expected Output: 0
    }
}

