import java.util.Arrays;

public class Knapsack {

    /**
     * Calculates the maximum value that can be obtained by selecting items with given weights and values,
     * such that the total weight of selected items does not exceed the knapsack capacity.
     *
     * @param weights An array of item weights.
     * @param values  An array of item values.
     * @param capacity The maximum weight the knapsack can hold.
     * @return The maximum value that can be obtained.
     *
     * Time Complexity: O(N * W), where N is the number of items and W is the knapsack capacity.
     * Space Complexity: O(N * W) due to the dp table.  Can be optimized to O(W) with a 1D DP array.
     */
    public static int knapsack(int[] weights, int[] values, int capacity) {
        int n = weights.length;

        // dp[i][w] stores the maximum value achievable using items up to index i with a maximum weight of w.
        int[][] dp = new int[n + 1][capacity + 1];

        // Initialize the first row and column to 0 (no items or no capacity).
        for (int i = 0; i <= n; i++) {
            dp[i][0] = 0;
        }
        for (int w = 0; w <= capacity; w++) {
            dp[0][w] = 0;
        }

        // Build the dp table iteratively.
        for (int i = 1; i <= n; i++) {
            for (int w = 1; w <= capacity; w++) {
                // If the current item's weight is less than or equal to the current capacity,
                // we have two choices:
                // 1. Include the current item:  Add its value to the maximum value achievable with the remaining capacity.
                // 2. Exclude the current item:  Use the maximum value achievable without the current item.
                if (weights[i - 1] <= w) {
                    dp[i][w] = Math.max(values[i - 1] + dp[i - 1][w - weights[i - 1]], dp[i - 1][w]);
                } else {
                    // If the current item's weight is greater than the current capacity, we cannot include it.
                    dp[i][w] = dp[i - 1][w];
                }
            }
        }

        return dp[n][capacity];
    }

    /**
     * A space-optimized version of the knapsack algorithm using a 1D DP array.
     *
     * @param weights An array of item weights.
     * @param values  An array of item values.
     * @param capacity The maximum weight the knapsack can hold.
     * @return The maximum value that can be obtained.
     *
     * Time Complexity: O(N * W), where N is the number of items and W is the knapsack capacity.
     * Space Complexity: O(W) due to the dp array.
     */
    public static int knapsackOptimized(int[] weights, int[] values, int capacity) {
        int n = weights.length;
        int[] dp = new int[capacity + 1];

        for (int i = 0; i < n; i++) {
            for (int w = capacity; w >= weights[i]; w--) {
                dp[w] = Math.max(dp[w], dp[w - weights[i]] + values[i]);
            }
        }

        return dp[capacity];
    }

    public static void main(String[] args) {
        // Test cases
        int[] weights1 = {1, 3, 4, 5};
        int[] values1 = {1, 4, 5, 7};
        int capacity1 = 7;
        System.out.println("Test Case 1: " + knapsack(weights1, values1, capacity1)); // Expected: 9
