import java.util.Arrays;

public class MinSwapsContiguousOnes {

    /**
     * Calculates the minimum number of swaps required to make all 1s contiguous in a binary array.
     *
     * @param arr The input binary array.
     * @return The minimum number of swaps required.
     *
     * Time Complexity: O(N), where N is the length of the array.  We iterate through the array
     *  a constant number of times.
     * Space Complexity: O(1). We use a constant amount of extra space.
     */
    public static int minSwaps(int[] arr) {
        int n = arr.length;
        int onesCount = 0;

        // Count the total number of 1s in the array.
        for (int num : arr) {
            if (num == 1) {
                onesCount++;
            }
        }

        // If there are no 1s or all elements are 1s, no swaps are needed.
        if (onesCount == 0 || onesCount == n) {
            return 0;
        }

        int windowOnes = 0;
        // Count the number of 1s in the initial window of size 'onesCount'.
        for (int i = 0; i < onesCount; i++) {
            if (arr[i] == 1) {
                windowOnes++;
            }
        }

        int maxOnes = windowOnes; // Keep track of the maximum number of 1s found in any window.

        // Slide the window through the array and update the 'maxOnes' count.
        for (int i = onesCount; i < n; i++) {
            if (arr[i] == 1) {
                windowOnes++;
            }
            if (arr[i - onesCount] == 1) {
                windowOnes--;
            }
            maxOnes = Math.max(maxOnes, windowOnes);
        }

        // The minimum number of swaps is the difference between the total number of 1s
        // and the maximum number of 1s found in a contiguous window.
        return onesCount - maxOnes;
    }

    public static void main(String[] args) {
        // Test cases
        int[] arr1 = {1, 0, 1, 0, 1};
        System.out.println("Input: " + Arrays.toString(arr1) + ", Min swaps: " + minSwaps(arr1)); // Expected: 1

        int[] arr2 = {1, 0, 1, 0, 1, 1};
        System.out.println("Input: " + Arrays.toString(arr2) + ", Min swaps: " + minSwaps(arr2)); // Expected: 1

        int[] arr3 = {0, 0, 0, 1, 0};
        System.out.println("Input: " + Arrays.toString(arr3) + ", Min swaps: " + minSwaps(arr3)); // Expected: 0

        int[] arr4 = {1, 1, 1, 1, 1};
        System.out.println("Input: " + Arrays.toString(arr4) + ", Min swaps: " + minSwaps(arr4)); // Expected: 0

        int[] arr5 = {0, 0, 0, 0, 0};
        System.out.println("Input: " + Arrays.toString(arr5) + ", Min swaps: " + minSwaps(arr5)); // Expected: 0

        int[] arr6 = {1, 0, 0, 1, 0, 1, 0, 1};
        System.out.println("Input: " + Arrays.toString(arr6) + ", Min swaps: " + minSwaps(arr6)); // Expected: 2

        int[] arr7 = {0, 1, 1, 0, 0, 1};
        System.out.println("Input: " + Arrays.toString(arr7) + ", Min swaps: " + minSwaps(arr7)); // Expected: 1

