import java.util.Arrays;
import java.util.HashSet;
import java.util.Scanner;
import java.util.Set;

public class SecondLargestDistinct {

    /**
     * Finds the second largest distinct element in an array of integers.
     *
     * @param arr The input array of integers.
     * @return The second largest distinct element in the array.
     *         Returns Integer.MIN_VALUE if the array has fewer than two distinct elements.
     *         Returns Integer.MIN_VALUE if the input array is null or has less than 2 elements.
     */
    public static int findSecondLargestDistinct(int[] arr) {
        if (arr == null || arr.length < 2) {
            return Integer.MIN_VALUE; // Handle null or small arrays
        }

        // Use a set to efficiently track distinct elements.
        Set<Integer> distinctElements = new HashSet<>();
        for (int num : arr) {
            distinctElements.add(num);
        }

        if (distinctElements.size() < 2) {
            return Integer.MIN_VALUE; // Handle arrays with fewer than two distinct elements
        }

        // Find the largest and second largest elements.  Initialize both to the smallest possible integer value
        int largest = Integer.MIN_VALUE;
        int secondLargest = Integer.MIN_VALUE;

        for (int num : distinctElements) {
            if (num > largest) {
                secondLargest = largest;
                largest = num;
            } else if (num > secondLargest && num != largest) {
                secondLargest = num;
            }
        }

        return secondLargest;
    }

    /**
     * Main method for testing the findSecondLargestDistinct method.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test cases
