        int[] arr10 = {Integer.MIN_VALUE, Integer.MIN_VALUE + 1, Integer.MIN_VALUE};
        System.out.println("Input: " + Arrays.toString(arr10) + ", Second Largest: " + findSecondLargestDistinct(arr10)); // Expected: Integer.MIN_VALUE + 1

        int[] arr11 = {1, 1};
        System.out.println("Input: " + Arrays.toString(arr11) + ", Second Largest: " + findSecondLargestDistinct(arr11)); // Expected: Integer.MIN_VALUE

        int[] arr12 = null;
        System.out.println("Input: null, Second Largest: " + findSecondLargestDistinct(arr12)); // Expected: Integer.MIN_VALUE

