import java.util.Scanner;

public class RemoveNthNodeFromEnd {

    // Node class for the linked list
    static class ListNode {
        int val;
        ListNode next;

        ListNode(int val) {
            this.val = val;
        }
    }

    /**
     * Removes the nth node from the end of a singly linked list.
     *
     * @param head The head of the linked list.
     * @param n    The position from the end of the list to remove (1-indexed).
     * @return The head of the modified linked list.
     */
    public static ListNode removeNthFromEnd(ListNode head, int n) {
        // Create a dummy node to handle the case where the head needs to be removed.
        ListNode dummy = new ListNode(0);
        dummy.next = head;

        // Use two pointers: 'fast' and 'slow'.
        ListNode fast = dummy;
        ListNode slow = dummy;

        // Move 'fast' pointer n nodes ahead.
        for (int i = 0; i <= n; i++) {
            if (fast == null) {
                return head; // Handle the case where n is larger than the list length
            }
            fast = fast.next;
        }

        // Move both 'fast' and 'slow' pointers until 'fast' reaches the end.
        while (fast != null) {
            fast = fast.next;
            slow = slow.next;
        }

        // 'slow' is now pointing to the node before the nth node from the end.
        // Remove the nth node from the end.
        slow.next = slow.next.next;

        return dummy.next; // Return the new head (in case the original head was removed)
    }

    /**
     * Converts an array of integers to a linked list.
     *
     * @param arr The array of integers.
     * @return The head of the created linked list.
     */
    public static ListNode arrayToLinkedList(int[] arr) {
        if (arr == null || arr.length == 0) {
            return null;
        }

        ListNode head = new ListNode(arr[0]);
        ListNode current = head;

        for (int i = 1; i < arr.length; i++) {
            current.next = new ListNode(arr[i]);
            current = current.next;
        }

        return head;
    }

    /**
     * Converts a linked list to a string representation for printing.
     *
     * @param head The head of the linked list.
     * @return A string representation of the linked list.
     */
    public static String linkedListToString(ListNode head) {
        StringBuilder sb = new StringBuilder();
        ListNode current = head;

        while (current != null) {
            sb.append(current.val);
            if (current.next != null) {
                sb.append(" ");
            }
            current = current.next;
        }

        return sb.toString();
    }

