import java.util.PriorityQueue;
import java.util.Random;

public class KthSmallestElement {

    /**
     * Finds the k-th smallest element in an unsorted array.
     *
     * @param arr The input array of integers.
     * @param k   The desired k-th smallest element (1-based index).
     * @return The k-th smallest element in the array.
     * @throws IllegalArgumentException if the input array is null or empty, or if k is invalid.
     */
    public int findKthSmallest(int[] arr, int k) {
        // Input validation
        if (arr == null || arr.length == 0) {
            throw new IllegalArgumentException("Input array cannot be null or empty.");
        }
        if (k <= 0 || k > arr.length) {
            throw new IllegalArgumentException("Invalid value of k. k must be between 1 and the array length.");
        }

        // Use a max-heap of size k to store the k smallest elements seen so far.
        // The root of the max-heap will be the k-th smallest element.
        PriorityQueue<Integer> maxHeap = new PriorityQueue<>((a, b) -> b - a); // Max-heap using a custom comparator

        for (int num : arr) {
            if (maxHeap.size() < k) {
                maxHeap.offer(num);
            } else if (num < maxHeap.peek()) {
                maxHeap.poll(); // Remove the largest element in the heap
                maxHeap.offer(num); // Add the current element
            }
        }

        // After iterating through the array, the root of the max-heap is the k-th smallest element.
        return maxHeap.peek();
    }

    /**
     * A slightly faster implementation using quickselect (Hoare's selection algorithm).
     * This method modifies the input array in place.
     *
     * @param arr The input array of integers.
     * @param k   The desired k-th smallest element (1-based index).
     * @return The k-th smallest element in the array.
     * @throws IllegalArgumentException if the input array is null or empty, or if k is invalid.
     */
    public int findKthSmallestQuickselect(int[] arr, int k) {
        if (arr == null || arr.length == 0) {
            throw new IllegalArgumentException("Input array cannot be null or empty.");
        }
        if (k <= 0 || k > arr.length) {
            throw new IllegalArgumentException("Invalid value of k. k must be between 1 and the array length.");
        }

        return quickSelect(arr, 0, arr.length - 1, k);
    }

    private int quickSelect(int[] arr, int low, int high, int k) {
        if (low <= high) {
            int partitionIndex = partition(arr, low, high);

            if (partitionIndex == k - 1) {
                return arr[partitionIndex];
            } else if (partitionIndex > k - 1) {
                return quickSelect(arr, low, partitionIndex - 1, k);
            } else {
                return quickSelect(arr, partitionIndex + 1, high, k);
            }
        }
        return -1; // Should not reach here if k is valid. Added for completeness.
    }

    private int partition(int[] arr, int low, int high) {
        // Choose a pivot (can be improved with median-of-three)
        int pivot = arr[high];
        int i = (low - 1); // Index of smaller element

        for (int j = low; j <= high - 1; j++) {
            // If current element is smaller than or equal to pivot
            if (arr[j] <= pivot) {
                i++;

                // swap arr[i] and arr[j]
                int temp = arr[i];
                arr[i] = arr[j];
                arr[j] = temp;
            }
        }

        // swap arr[i + 1] and arr[high] (or pivot)
        int temp = arr[i + 1];
        arr[i + 1] = arr[high];
        arr[high] = temp;

        return (i + 1);
    }


    public static void main(String[] args) {
        KthSmallestElement kthFinder = new KthSmallestElement();

        // Test cases
        int[] arr1 = {7, 10, 4, 20, 15};
        int k1 = 4;
        System.out.println("Array: [7, 10, 4, 20, 15], k = 4, Kth smallest (Heap): " + kthFinder.findKthSmallest(arr1, k1));
        System.out.println("Array: [7, 10, 4, 20, 15], k = 4, Kth smallest (Quickselect): " + kthFinder.findKthSmallestQuickselect(arr1.clone(), k1));


        int[] arr2 = {1, 2, 3, 4, 5};
        int k2 = 1;
        System.out.println("Array: [1, 2, 3, 4, 5], k = 1, Kth smallest (Heap): " + kthFinder.findKthSmallest(arr2, k2));
        System.out.println("Array: [1, 2, 3, 4, 5], k = 1, Kth smallest (Quickselect): " + kthFinder.findKthSmallestQuickselect(arr2.clone(), k2));



