        int k3 = 5;
        System.out.println("Array: [5, 4, 3, 2, 1], k = 5, Kth smallest (Heap): " + kthFinder.findKthSmallest(arr3, k3));
        System.out.println("Array: [5, 4, 3, 2, 1], k = 5, Kth smallest (Quickselect): " + kthFinder.findKthSmallestQuickselect(arr3.clone(), k3));


