        System.out.println("Array: [1], k = 1, Kth smallest (Quickselect): " + kthFinder.findKthSmallestQuickselect(arr5.clone(), k5));



        int[] arr6 = {5, 2, 9, 1, 5, 6};
        int k6 = 3;
