public class GradeCalculator {

    /**
     * Calculates the grade based on the given marks.
     *
     * @param marks The student's marks (0-100).
     * @return The corresponding letter grade.
     */
    public static String calculateGrade(int marks) {
        // Input validation: Ensure marks are within the valid range.
        if (marks < 0 || marks > 100) {
            return "Invalid Input"; // Handle invalid input gracefully
        }

        // Use a series of if-else if statements for efficient grading
        if (marks >= 90) {
            return "A+";
        } else if (marks >= 80) {
            return "A";
        } else if (marks >= 70) {
            return "B";
        } else if (marks >= 60) {
            return "C";
        } else if (marks >= 50) {
            return "D";
        } else {
            return "F";
        }
    }

    public static void main(String[] args) {
        // Test cases demonstrating the grade calculation
        System.out.println("Grade for 95: " + calculateGrade(95)); // Expected: A+
