        System.out.println("Grade for 75: " + calculateGrade(75)); // Expected: B
        System.out.println("Grade for 65: " + calculateGrade(65)); // Expected: C
        System.out.println("Grade for 55: " + calculateGrade(55)); // Expected: D
        System.out.println("Grade for 45: " + calculateGrade(45)); // Expected: F
        System.out.println("Grade for 100: " + calculateGrade(100)); // Expected: A+ (Edge case)
        System.out.println("Grade for 0: " + calculateGrade(0));   // Expected: F (Edge case)
        System.out.println("Grade for 80: " + calculateGrade(80)); // Expected: A (Edge case)
        System.out.println("Grade for 90: " + calculateGrade(90)); // Expected: A+ (Edge case)
        System.out.println("Grade for -5: " + calculateGrade(-5)); // Expected: Invalid Input (Negative marks)
        System.out.println("Grade for 105: " + calculateGrade(105)); // Expected: Invalid Input (Marks > 100)
