import java.util.Arrays;

public class MinCostPath {

    /**
     * Calculates the minimum cost to reach a target cell (x, y) from (0, 0) in a cost matrix.
     * Allowed moves are down, right, and diagonally down-right.
     *
     * @param costMatrix The cost matrix of size m x n.
     * @param x          The row index of the target cell.
     * @param y          The column index of the target cell.
     * @return The minimum total cost to reach the target cell.
     *
     * Time Complexity: O(m*n), where m is the number of rows and n is the number of columns.
     * Space Complexity: O(m*n) for the dp table.
     */
    public static int minCost(int[][] costMatrix, int x, int y) {
        int m = costMatrix.length;
        int n = costMatrix[0].length;

        // dp[i][j] stores the minimum cost to reach cell (i, j) from (0, 0)
        int[][] dp = new int[m][n];

        // Initialize the first cell
        dp[0][0] = costMatrix[0][0];

        // Initialize the first row
        for (int j = 1; j < n; j++) {
            dp[0][j] = dp[0][j - 1] + costMatrix[0][j];
        }

        // Initialize the first column
        for (int i = 1; i < m; i++) {
            dp[i][0] = dp[i - 1][0] + costMatrix[i][0];
        }

        // Fill the dp table
        for (int i = 1; i < m; i++) {
            for (int j = 1; j < n; j++) {
                dp[i][j] = costMatrix[i][j] + Math.min(dp[i - 1][j - 1], Math.min(dp[i - 1][j], dp[i][j - 1]));
            }
        }

        return dp[x][y];
    }

    public static void main(String[] args) {
        // Test cases
        int[][] costMatrix1 = {
                {1, 2, 3},
                {4, 8, 2},
                {1, 5, 3}
        };
        int x1 = 2, y1 = 2;
        System.out.println("Test Case 1: " + minCost(costMatrix1, x1, y1)); // Expected: 11

        int[][] costMatrix2 = {
                {1, 2, 3},
                {4, 5, 6},
                {7, 8, 9}
        };
        int x2 = 1, y2 = 1;
        System.out.println("Test Case 2: " + minCost(costMatrix2, x2, y2)); // Expected: 7

        int[][] costMatrix3 = {
                {10, 5, 2},
                {3, 1, 9},
                {6, 4, 7}
        };
        int x3 = 0, y3 = 0;
        System.out.println("Test Case 3: " + minCost(costMatrix3, x3, y3)); // Expected: 10

        int[][] costMatrix4 = {
                {1}
        };
        int x4 = 0, y4 = 0;
        System.out.println("Test Case 4 (Single Cell): " + minCost(costMatrix4, x4, y4)); // Expected: 1

        int[][] costMatrix5 = {
                {1, 2},
                {3, 4}
        };
        int x5 = 1, y5 = 1;
        System.out.println("Test Case 5: " + minCost(costMatrix5, x5, y5)); // Expected: 7

        int[][] costMatrix6 = {
                {4, 7, 8, 6, 4},
                {6, 7, 3, 9, 2},
                {3, 8, 1, 2, 4},
                {7, 1, 7, 3, 7},
                {2, 9, 8, 9, 3}
        };
        int x6 = 4, y6 = 4;
        System.out.println("Test Case 6: " + minCost(costMatrix6, x6, y6)); // Expected: 21

