import java.math.BigDecimal;
import java.math.BigInteger;

public class DecimalToFraction {

    /**
     * Converts a decimal number to an irreducible fraction.
     *
     * @param n The decimal number to convert.
     * @return A string representation of the irreducible fraction in the form "numerator/denominator".
     */
    public static String decimalToIrreducibleFraction(double n) {
        // Use BigDecimal to handle decimal precision accurately
        BigDecimal decimal = new BigDecimal(String.valueOf(n));

        // Find the number of decimal places
        int scale = decimal.scale();

        // Calculate the numerator and denominator
        BigInteger numerator = decimal.multiply(BigDecimal.valueOf(10).pow(scale)).toBigInteger();
        BigInteger denominator = BigInteger.valueOf(10).pow(scale);

        // Find the greatest common divisor (GCD) to reduce the fraction
        BigInteger gcd = numerator.gcd(denominator);

        // Divide both numerator and denominator by the GCD
        BigInteger reducedNumerator = numerator.divide(gcd);
        BigInteger reducedDenominator = denominator.divide(gcd);

        return reducedNumerator.toString() + "/" + reducedDenominator.toString();
