import java.util.Scanner;

/**
 * Solution to generate a concentric square pattern.
 * The outermost layer contains the number N, the next inner layer contains N-1,
 * and so on until the center cell, which contains 1.
 */
public class ConcentricSquarePattern {

    /**
     * Generates and prints the concentric square pattern.
     *
     * @param n The input integer determining the size of the pattern.
     */
    public static void printConcentricPattern(int n) {
        int size = 2 * n - 1;
        int[][] pattern = new int[size][size];

        // Fill the pattern
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                int minDistance = Math.min(Math.min(i, j), Math.min(size - 1 - i, size - 1 - j));
                pattern[i][j] = n - minDistance;
            }
        }

        // Print the pattern
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                System.out.print(pattern[i][j] + (j == size - 1 ? "" : " "));
            }
            System.out.println();
        }
    }

    /**
     * Optimized method to generate and print the concentric square pattern without extra space
     *
     * @param n The input integer determining the size of the pattern.
     */
    public static void printConcentricPatternOptimized(int n) {
        int size = 2 * n - 1;

        // Print the pattern
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                int minDistance = Math.min(Math.min(i, j), Math.min(size - 1 - i, size - 1 - j));
                System.out.print((n - minDistance) + (j == size - 1 ? "" : " "));
            }
            System.out.println();
        }
    }

    /**
