import java.util.*;
import java.lang.*;

class ConnectedComponents {

    /**
     * Performs Depth First Search (DFS) to find connected components in a graph.
     *
     * @param V The number of vertices in the graph.
     * @param adj An adjacency list representing the graph.
     * @return A list of lists, where each inner list represents a connected component
     *         with vertices sorted in ascending order. The outer list is sorted based
     *         on the smallest vertex in each component.
     *
     * Time Complexity: O(V + E), where V is the number of vertices and E is the number of edges.
     * Space Complexity: O(V), where V is the number of vertices (for visited array and stack during DFS).
     */
    public static List<List<Integer>> connectedComponents(int V, ArrayList<ArrayList<Integer>> adj) {
        List<List<Integer>> components = new ArrayList<>();
        boolean[] visited = new boolean[V];

        // Iterate through all vertices to find unvisited components
        for (int i = 0; i < V; i++) {
            if (!visited[i]) {
                List<Integer> component = new ArrayList<>();
                dfs(i, adj, visited, component);
                Collections.sort(component); // Sort the vertices within the component

                components.add(component);
            }
        }

        // Sort the components based on their smallest vertex
        components.sort(Comparator.comparingInt(list -> list.get(0)));

        return components;
    }

    /**
     * Performs Depth First Search (DFS) starting from a given vertex.
     *
     * @param v       The starting vertex for DFS.
     * @param adj     An adjacency list representing the graph.
     * @param visited A boolean array to track visited vertices.
     * @param component  The list to store the vertices of the current connected component.
     *
     * Time Complexity: O(V + E) within the connected component.
     * Space Complexity: O(V) within the connected component (due to recursion stack in the worst case, and the component list).
     */
    private static void dfs(int v, ArrayList<ArrayList<Integer>> adj, boolean[] visited, List<Integer> component) {
        visited[v] = true;
        component.add(v);

        // Iterate through neighbors of the current vertex
        for (int neighbor : adj.get(v)) {
            if (!visited[neighbor]) {
                dfs(neighbor, adj, visited, component);
            }
        }
    }

