import java.util.Arrays;

public class UniqueSubsequences {

    /**
     * Calculates the total number of unique subsequences in a given string.
     *
     * @param s The input string.
     * @return The number of distinct subsequences, including the empty string.
     */
    public static int countDistinctSubsequences(String s) {
        int n = s.length();

        // dp[i] stores the number of distinct subsequences of s[0...i-1]
        long[] dp = new long[n + 1];

        // Empty string is always a subsequence
        dp[0] = 1;

        // lastOccurrence[char] stores the last index where 'char' was seen
        int[] lastOccurrence = new int[256]; // Assuming ASCII characters
        Arrays.fill(lastOccurrence, -1);

        // Iterate through the string
        for (int i = 1; i <= n; i++) {
            char currentChar = s.charAt(i - 1);

            // Number of subsequences up to current character
            dp[i] = 2 * dp[i - 1];

            // If the current character was seen before, subtract the subsequences ending at the previous occurrence
            if (lastOccurrence[currentChar] != -1) {
                dp[i] = dp[i] - dp[lastOccurrence[currentChar] - 1];
            }

            // Store the current index as the last occurrence of currentChar
            lastOccurrence[currentChar] = i;
        }

        return (int) dp[n];
    }


    public static void main(String[] args) {
        // Test cases
        testCase("gfg", 7);
        testCase("ggg", 4);
        testCase("abcabc", 22);
        testCase("aaaa", 5);
        testCase("", 1);  // Edge case: Empty string
        testCase("a", 2); // Edge case: Single character
        testCase("abcdefgh", 256); //Test case with all unique characters


    }

    private static void testCase(String s, int expected) {
        int result = countDistinctSubsequences(s);
        System.out.println("Input: " + s);
        System.out.println("Expected: " + expected);
        System.out.println("Actual: " + result);
        if (result == expected) {
            System.out.println("PASSED");
        } else {
            System.out.println("FAILED");
        }
        System.out.println("--------------------");
    }


    /*
     * Time Complexity: O(n), where n is the length of the string s. We iterate through the string once.
     * Space Complexity: O(1). We use a fixed-size array 'lastOccurrence' of size 256 (for ASCII characters)
     * and a dp array of size n + 1, but the lastOccurrence is constant size. So it can be said as O(n) in terms of dp array
     *  */
}
```

