        // Example 2
        str1 = "cat";
        str2 = "cut";
        result = editDistance.minDistance(str1, str2);
        System.out.println("Example 2: str1 = \"" + str1 + "\", str2 = \"" + str2 + "\", Edit Distance = " + result);  // Expected: 1

        // Example 3
