import java.util.*;

class TreeNode {
    int val;
    TreeNode left;
    TreeNode right;

    TreeNode(int val) {
        this.val = val;
    }
}

public class BinaryTreeSplit {

    /**
     * Removes a node with the given target value from a binary tree and returns a list of root values of the remaining subtrees.
     *
     * @param root   The root of the binary tree.
     * @param target The value of the node to remove.
     * @return A list of integers representing the root values of the remaining subtrees.
     *         If the target node is not present, returns a list containing only the original root value.
     */
    public List<Integer> splitBinaryTree(TreeNode root, int target) {
        List<Integer> result = new ArrayList<>();
        TreeNode newRoot = removeNode(root, target, result);

        // If the root was not removed (target not found or target was not root), add the root to the result if it's not null
        if (newRoot != null && !result.contains(newRoot.val)) {
            result.add(newRoot.val);
        }

        // Sort the result to ensure consistent output regardless of traversal order
        Collections.sort(result);

        return result;
    }

    /**
     * Helper method to recursively remove the target node from the tree.
     *
     * @param node   The current node being examined.
     * @param target The value of the node to remove.
     * @param result The list to store the root values of the remaining subtrees.
     * @return The root of the subtree after removal. Can be null if the current node is the target.
     */
    private TreeNode removeNode(TreeNode node, int target, List<Integer> result) {
        if (node == null) {
            return null;
        }

        // Recursively process left and right subtrees first
        node.left = removeNode(node.left, target, result);
        node.right = removeNode(node.right, target, result);

        // If the current node is the target, add its children to the result and return null to remove the current node
        if (node.val == target) {
            if (node.left != null) {
                result.add(node.left.val);
            }
            if (node.right != null) {
                result.add(node.right.val);
            }
            return null; // Remove the current node by returning null
        }

        return node; // Return the current node if it's not the target
    }

    /**
     * Helper method to construct a binary tree from a level-order integer array.
     *
     * @param arr An array of integers representing the node values in level-order.
     *            Use -1 to represent a null child.
     * @return The root of the constructed binary tree.
     */
    private TreeNode constructTree(Integer[] arr) {
        if (arr == null || arr.length == 0 || arr[0] == -1) {
            return null;
        }

        TreeNode root = new TreeNode(arr[0]);
        Queue<TreeNode> queue = new LinkedList<>();
        queue.offer(root);

        int i = 1;
        while (!queue.isEmpty() && i < arr.length) {
            TreeNode current = queue.poll();

            if (i < arr.length && arr[i] != -1) {
                current.left = new TreeNode(arr[i]);
                queue.offer(current.left);
            }
            i++;

            if (i < arr.length && arr[i] != -1) {
                current.right = new TreeNode(arr[i]);
                queue.offer(current.right);
            }
            i++;
        }

        return root;
    }

    public static void main(String[] args) {
        BinaryTreeSplit splitter = new BinaryTreeSplit();

        // Test Case 1
        Integer[] arr1 = {1, 2, 3, -1, -1, 4, 5};
        TreeNode root1 = splitter.constructTree(arr1);
        int target1 = 3;
        List<Integer> result1 = splitter.splitBinaryTree(root1, target1);
        System.out.println("Test Case 1: " + result1); // Expected: [1, 4, 5]

        // Test Case 2
        Integer[] arr2 = {10, 5, 15, -1, -1};
        TreeNode root2 = splitter.constructTree(arr2);
        int target2 = 5;
        List<Integer> result2 = splitter.splitBinaryTree(root2, target2);
        System.out.println("Test Case 2: " + result2); // Expected: [10]

        // Test Case 3: Target not found
        Integer[] arr3 = {1, 2, 3};
        TreeNode root3 = splitter.constructTree(arr3);
        int target3 = 4;
        List<Integer> result3 = splitter.splitBinaryTree(root3, target3);
        System.out.println("Test Case 3: " + result3); // Expected: [1]

        // Test Case 4: Root is the target
        Integer[] arr4 = {1, 2, 3};
        TreeNode root4 = splitter.constructTree(arr4);
        int target4 = 1;
        List<Integer> result4 = splitter.splitBinaryTree(root4, target4);
        System.out.println("Test Case 4: " + result4); // Expected: [2, 3]

        // Test Case 5: Empty tree
        Integer[] arr5 = {};
        TreeNode root5 = splitter.constructTree(arr5);
        int target5 = 1;
        List<Integer> result5 = splitter.splitBinaryTree(root5, target5);
        System.out.println("Test Case 5: " + result5); // Expected: []

        // Test Case 6: Only root node, target is not the root
        Integer[] arr6 = {1};
        TreeNode root6 = splitter.constructTree(arr6);
        int target6 = 2;
        List<Integer> result6 = splitter.splitBinaryTree(root6, target6);
        System.out.println("Test Case 6: " + result6); // Expected: [1]

        // Test Case 7: Only root node, target is the root
        Integer[] arr7 = {1};
        TreeNode root7 = splitter.constructTree(arr7);
        int target7 = 1;
        List<Integer> result7 = splitter.splitBinaryTree(root7, target7);
        System.out.println("Test Case 7: " + result7); // Expected: []

        // Test Case 8: Larger tree with removal in the middle
        Integer[] arr8 = {1,2,3,4,5,6,7,8,-1,9,10,-1,-1,11,-1};
        TreeNode root8 = splitter.constructTree(arr8);
        int target8 = 3;
        List<Integer> result8 = splitter.splitBinaryTree(root8, target8);
        System.out.println("Test Case 8: " + result8);  // Expected: [1, 6, 7]
    }

    /*
     * Time Complexity Analysis:
     *   - constructTree(): O(N), where N is the number of nodes in the tree. This is due to the level-order traversal to build the tree.
     *   - splitBinaryTree(): O(N) in the worst case, where N is the number of nodes. This is because the `removeNode` method performs a depth-first traversal and processes each node.
     *   - Collections.sort(): O(K log K) where K is the number of roots in result list.  K <= N in worst case.
     *   - Overall: O(N + K log K).  If K is small compared to N, this effectively becomes O(N)
     *
     * Space Complexity Analysis:
     *   - constructTree(): O(N) in the worst case due to the queue used for level-order traversal.  In a skewed tree this is reduced to O(1).
     *   - splitBinaryTree():
     *     - O(H) for the call stack during the recursive `removeNode` method, where H is the height of the tree. In the worst case (skewed tree), H = N, so the space complexity is O(N). In a balanced tree, it's O(log N).
     *     - O(K) for the result list, where K is the number of subtrees formed. In the worst case, K can be equal to N/2 (e.g. removing the root repeatedly)
     *   - Overall: O(max(N, K)) = O(N).  Where N is the number of nodes and K is number of items added to result list.
     */
}
```

