public class MinInsertDelete {

    /**
     * Calculates the minimum number of insertions and deletions required to convert str1 to str2.
     *
     * @param str1 The source string.
     * @param str2 The target string.
     * @return An array containing the minimum deletions and insertions, respectively.  Returns null if either string is null.
     */
    public int[] minInsertDelete(String str1, String str2) {
        if (str1 == null || str2 == null) {
            return null; // Handle null input strings
        }

        int n = str1.length();
        int m = str2.length();

        // Calculate the length of the longest common subsequence (LCS)
        int lcsLength = longestCommonSubsequenceLength(str1, str2);

        // Minimum number of deletions = Length of str1 - Length of LCS
        int deletions = n - lcsLength;

        // Minimum number of insertions = Length of str2 - Length of LCS
        int insertions = m - lcsLength;

        return new int[]{deletions, insertions};
    }

    /**
     * Helper method to calculate the length of the Longest Common Subsequence (LCS) of two strings.
     *
     * @param str1 The first string.
     * @param str2 The second string.
     * @return The length of the LCS.
     */
    private int longestCommonSubsequenceLength(String str1, String str2) {
        int n = str1.length();
        int m = str2.length();

        // dp[i][j] stores the length of LCS of str1[0..i-1] and str2[0..j-1]
        int[][] dp = new int[n + 1][m + 1];

        // Initialize the first row and column to 0
        for (int i = 0; i <= n; i++) {
            dp[i][0] = 0;
        }
        for (int j = 0; j <= m; j++) {
            dp[0][j] = 0;
        }

        // Fill the dp table
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                if (str1.charAt(i - 1) == str2.charAt(j - 1)) {
                    dp[i][j] = dp[i - 1][j - 1] + 1;
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                }
            }
        }

        return dp[n][m];
    }


