import java.util.PriorityQueue;
import java.util.Random;

public class KthLargestElement {

    /**
     * Finds the k-th largest element in an unsorted array.
     *
     * @param arr The input array of integers.
     * @param k   The k-th largest element to find (1 <= k <= arr.length).
     * @return The k-th largest element in the array.
     * @throws IllegalArgumentException if the input array is null or empty, or if k is invalid.
     */
    public int findKthLargest(int[] arr, int k) {
        if (arr == null || arr.length == 0) {
            throw new IllegalArgumentException("Input array cannot be null or empty.");
        }
        if (k <= 0 || k > arr.length) {
            throw new IllegalArgumentException("Invalid value of k: " + k + ". k must be between 1 and " + arr.length + " inclusive.");
        }

        // Using Min-Heap (PriorityQueue)
        PriorityQueue<Integer> minHeap = new PriorityQueue<>();
        for (int num : arr) {
            minHeap.add(num);
            if (minHeap.size() > k) {
                minHeap.poll(); // Remove the smallest element
            }
        }

        return minHeap.peek(); // The smallest element in the heap is the k-th largest
    }

    /**
     * Finds the k-th largest element in an unsorted array using QuickSelect algorithm.
     * This provides better average-case performance.
     *
     * @param arr The input array of integers.
     * @param k   The k-th largest element to find (1 <= k <= arr.length).
     * @return The k-th largest element in the array.
     * @throws IllegalArgumentException if the input array is null or empty, or if k is invalid.
     */
    public int findKthLargestQuickSelect(int[] arr, int k) {
        if (arr == null || arr.length == 0) {
            throw new IllegalArgumentException("Input array cannot be null or empty.");
        }
        if (k <= 0 || k > arr.length) {
            throw new IllegalArgumentException("Invalid value of k: " + k + ". k must be between 1 and " + arr.length + " inclusive.");
        }

        return quickSelect(arr, 0, arr.length - 1, arr.length - k); // k-th largest is (n-k)-th smallest
    }

    private int quickSelect(int[] arr, int low, int high, int k) {
        if (low == high) {
            return arr[low];
        }

        int partitionIndex = partition(arr, low, high);

        if (k == partitionIndex) {
            return arr[k];
        } else if (k < partitionIndex) {
            return quickSelect(arr, low, partitionIndex - 1, k);
        } else {
            return quickSelect(arr, partitionIndex + 1, high, k);
        }
    }

    private int partition(int[] arr, int low, int high) {
        Random random = new Random();
        int pivotIndex = low + random.nextInt(high - low + 1); // Choose a random pivot to avoid worst-case scenarios
        int pivot = arr[pivotIndex];

        swap(arr, pivotIndex, high); // Move pivot to the end

        int i = low;
        for (int j = low; j < high; j++) {
            if (arr[j] <= pivot) {
                swap(arr, i, j);
                i++;
            }
        }

        swap(arr, i, high); // Move pivot to its correct sorted position
        return i;
    }

    private void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }


    public static void main(String[] args) {
        KthLargestElement kthLargest = new KthLargestElement();

        // Test cases
        int[] arr1 = {7, 10, 4, 3, 20, 15};
        int k1 = 3;
