import java.time.LocalDate;
import java.util.*;

public class CouponSystem {

    // Data Layers (In-Memory Data Structures)
    private final Map<String, Coupon> coupons = new HashMap<>();
    private final Map<String, Seller> sellers = new HashMap<>();
    private final Map<String, Product> products = new HashMap<>();

    // Service Layers
    public String createCoupon(String code, double discountValue, DiscountType discountType, LocalDate validity,
                               CouponApplicability applicability, String sellerId, String productId,
                               Integer minProducts, Double minCartValue) {
        // Input validation
        if (code == null || code.isEmpty() || discountValue <= 0 || validity == null || applicability == null) {
            throw new IllegalArgumentException("Invalid coupon creation parameters.");
        }

        if (coupons.containsKey(code)) {
            throw new IllegalArgumentException("Coupon with code " + code + " already exists.");
        }
        if ((applicability == CouponApplicability.SELLER_SPECIFIC && !sellers.containsKey(sellerId)) ||
                (applicability == CouponApplicability.PRODUCT_SPECIFIC && !products.containsKey(productId))) {
            throw new IllegalArgumentException("Seller or product doesn't exist");
        }

        Coupon coupon = new Coupon(code, discountValue, discountType, validity, applicability, sellerId, productId, minProducts, minCartValue);
        coupons.put(code, coupon);
        return "Coupon created successfully with code: " + code;
    }


    public double applyCoupon(String couponCode, String sellerId, List<Product> cart, double cartValue) {
        if (couponCode == null || couponCode.isEmpty() || cart == null || cart.isEmpty()) {
            throw new IllegalArgumentException("Invalid coupon application parameters.");
        }

        Coupon coupon = coupons.get(couponCode);
        if (coupon == null) {
            System.out.println("Coupon not found");
            return cartValue; // Return original cart value if coupon is not found
        }

        if (LocalDate.now().isAfter(coupon.getValidity())) {
            System.out.println("Coupon is expired");
            return cartValue; // Return original cart value if coupon is expired
        }

        if (coupon.getMinProducts() != null && cart.size() < coupon.getMinProducts()) {
            System.out.println("Minimum product count not reached");
            return cartValue;
        }

        if (coupon.getMinCartValue() != null && cartValue < coupon.getMinCartValue()) {
            System.out.println("Minimum cart value not reached");
            return cartValue;
        }

        switch (coupon.getApplicability()) {
            case GLOBAL:
                break; // No specific checks needed
            case SELLER_SPECIFIC:
                if (!coupon.getSellerId().equals(sellerId)) {
                    System.out.println("Coupon not applicable for this seller");
                    return cartValue;
                }
                break;
            case PRODUCT_SPECIFIC:
                boolean productFound = false;
                for (Product product : cart) {
                    if (coupon.getProductId().equals(product.getId())) {
                        productFound = true;
                        break;
                    }
                }
                if (!productFound) {
                    System.out.println("Coupon not applicable for products in cart");
                    return cartValue;
                }
                break;
        }

        double discountedValue;
        if (coupon.getDiscountType() == DiscountType.PERCENTAGE) {
            discountedValue = cartValue * (1 - coupon.getDiscountValue() / 100);
        } else {
            discountedValue = Math.max(0, cartValue - coupon.getDiscountValue()); // Ensure discount doesn't make the value negative
        }

        return discountedValue;
    }


