public class TriangleHops {

    /**
     * Calculates the number of distinct ways to return to vertex A after N hops,
     * starting from vertex A, where each hop must move to a different vertex.
     *
     * @param n The number of hops.
     * @return The number of distinct ways to return to vertex A.
     */
    public long countWays(int n) {
        // Base case: If n is 0, there's one way (stay at A).  If n is 1, no way to return to A.
        if (n == 0) {
            return 1;  // Considered as returning after 0 hops. Could also be 0 if we strictly exclude this.
        }
        if (n == 1) {
            return 0;
        }

        // Dynamic programming approach.
        // waysToA[i] stores the number of ways to reach A after i hops.
        // waysToNotA[i] stores the number of ways to reach B or C after i hops.
        long[] waysToA = new long[n + 1];
        long[] waysToNotA = new long[n + 1];

        // Initialization:
        waysToA[0] = 1;  // Start at A, 0 hops.
        waysToNotA[0] = 0; // Impossible to be at B or C after 0 hops.

        // Iteration:
        for (int i = 1; i <= n; i++) {
            // To reach A after i hops, you must have been at B or C after i-1 hops.
            waysToA[i] = waysToNotA[i - 1];

            // To reach B or C after i hops, you can either:
            // 1. Come from A after i-1 hops (and there are waysToA[i-1] ways to do this).
            // 2. Come from the other of B or C after i-1 hops (there are waysToNotA[i-1] ways to reach B/C from start,
            //     each could go to another one so stays the same).
            waysToNotA[i] = waysToA[i - 1] + waysToNotA[i - 1];
        }

        return waysToA[n];
    }

    /**
     * Optimized version using constant space.  Leverages the fact that only the previous
     * two values are needed.
     * @param n The number of hops.
     * @return The number of distinct ways to return to vertex A.
     */
    public long countWaysOptimized(int n) {
        if (n == 0) {
            return 1; // Special handling for 0 hops.
        }
        if (n == 1) {
            return 0;
        }

        long waysToA = 1; // waysToA[0]
        long waysToNotA = 0; // waysToNotA[0]

        for (int i = 1; i <= n; i++) {
            long newWaysToA = waysToNotA;
            long newWaysToNotA = waysToA + waysToNotA;

            waysToA = newWaysToA;
            waysToNotA = newWaysToNotA;
        }

        return waysToA;
    }


    public static void main(String[] args) {
        TriangleHops triangleHops = new TriangleHops();

        // Test cases
        System.out.println("N = 0: " + triangleHops.countWaysOptimized(0));    // Expected: 1
        System.out.println("N = 1: " + triangleHops.countWaysOptimized(1));    // Expected: 0
        System.out.println("N = 2: " + triangleHops.countWaysOptimized(2));    // Expected: 2
        System.out.println("N = 3: " + triangleHops.countWaysOptimized(3));    // Expected: 2
        System.out.println("N = 4: " + triangleHops.countWaysOptimized(4));    // Expected: 6
        System.out.println("N = 5: " + triangleHops.countWaysOptimized(5));    // Expected: 10
        System.out.println("N = 6: " + triangleHops.countWaysOptimized(6));    // Expected: 22
        System.out.println("N = 7: " + triangleHops.countWaysOptimized(7));    // Expected: 42
        System.out.println("N = 8: " + triangleHops.countWaysOptimized(8));    // Expected: 86
        System.out.println("N = 30: " + triangleHops.countWaysOptimized(30));  // Test with constraint max value
    }
}
