        // Test Case 2
        TreeNode root2 = new TreeNode(0);
        root2.left = new TreeNode(0);
        root2.left.left = new TreeNode(0);
        root2.left.left.left = new TreeNode(0);
        root2.left.left.left.right = new TreeNode(0);

        System.out.println("Test Case 2: " + solution.minCameras(root2)); // Expected Output: 2

        // Test Case 3: Empty Tree
        TreeNode root3 = null;
        System.out.println("Test Case 3: " + solution.minCameras(root3)); // Expected Output: 0

        // Test Case 4: Single Node Tree
        TreeNode root4 = new TreeNode(0);
        System.out.println("Test Case 4: " + solution.minCameras(root4)); // Expected Output: 1

        // Test Case 5: More complex test case
        TreeNode root5 = new TreeNode(0);
        root5.left = new TreeNode(0);
        root5.right = new TreeNode(0);
        root5.left.left = new TreeNode(0);
        root5.left.right = new TreeNode(0);
        root5.right.left = new TreeNode(0);
        root5.right.right = new TreeNode(0);
        root5.left.left.left = new TreeNode(0);
        root5.left.left.right = new TreeNode(0);
        System.out.println("Test Case 5: " + solution.minCameras(root5)); // Expected Output: 2

        //Test Case 6
