import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.locks.Lock;
import java.util.concurrent.locks.ReentrantLock;

public class FoodOrderingSystem {

    // In-memory data structures
    private final Map<String, Restaurant> restaurants = new ConcurrentHashMap<>(); //Restaurant Name, Restaurant Object. Thread-safe
    private final Map<String, Order> orders = new ConcurrentHashMap<>(); // OrderId, Order Object. Thread-safe

    // Unique order ID generator
    private static int orderIdCounter = 0;
    private static synchronized String generateOrderId() {
        return "ORDER_" + (++orderIdCounter);
    }

    // Restaurant selection strategies
    public interface RestaurantSelectionStrategy {
        Restaurant selectRestaurant(List<Restaurant> availableRestaurants, Order order);
    }

    public static class LowestCostStrategy implements RestaurantSelectionStrategy {
        @Override
        public Restaurant selectRestaurant(List<Restaurant> availableRestaurants, Order order) {
            return availableRestaurants.stream()
                    .min(Comparator.comparingDouble(restaurant -> calculateOrderCost(restaurant, order)))
                    .orElse(null);
        }

        private double calculateOrderCost(Restaurant restaurant, Order order) {
            double cost = 0;
            for (Map.Entry<String, Integer> itemEntry : order.getItems().entrySet()) {
                String itemName = itemEntry.getKey();
                int quantity = itemEntry.getValue();
                if (restaurant.getMenu().containsKey(itemName)) {
                    cost += restaurant.getMenu().get(itemName) * quantity;
                } else {
                    return Double.MAX_VALUE; // Restaurant can't fulfill the order
                }
            }
            return cost;
        }
    }

    public static class HighestRatingStrategy implements RestaurantSelectionStrategy {
        @Override
        public Restaurant selectRestaurant(List<Restaurant> availableRestaurants, Order order) {
            return availableRestaurants.stream()
                    .max(Comparator.comparingDouble(Restaurant::getRating))
                    .orElse(null);
        }
    }

    // Core functionalities
    public void onboardRestaurant(Restaurant restaurant) {
        if (restaurants.containsKey(restaurant.getName())) {
            throw new IllegalArgumentException("Restaurant with name " + restaurant.getName() + " already exists.");
        }
        restaurants.put(restaurant.getName(), restaurant);
    }

    public String placeOrder(String user, Map<String, Integer> items, String selectionCriteria) {
        RestaurantSelectionStrategy strategy;
        switch (selectionCriteria.toLowerCase()) {
            case "lowest cost":
                strategy = new LowestCostStrategy();
                break;
            case "highest rating":
                strategy = new HighestRatingStrategy();
                break;
            default:
                throw new IllegalArgumentException("Invalid selection criteria: " + selectionCriteria);
        }

        Order order = new Order(generateOrderId(), user, items, Order.OrderStatus.PENDING);
        List<Restaurant> availableRestaurants = findRestaurantsThatCanFulfillOrder(order);

        if (availableRestaurants.isEmpty()) {
            return "Order can’t be fulfilled (since none of the restaurants serve all the items).";
        }

        Restaurant selectedRestaurant = strategy.selectRestaurant(availableRestaurants, order);

        if (selectedRestaurant == null) {
            return "No restaurant available to fulfill the order based on the selection criteria.";
        }

        synchronized (selectedRestaurant) {
            if (selectedRestaurant.getProcessingOrders().size() < selectedRestaurant.getMaxOrders()) {
                selectedRestaurant.getProcessingOrders().add(order.getOrderId());
                order.setStatus(Order.OrderStatus.ACCEPTED);
                orders.put(order.getOrderId(), order);
                order.setRestaurantName(selectedRestaurant.getName());
                return "Order assigned to " + selectedRestaurant.getName();
            } else {
                return "No restaurant available to fulfill the order based on capacity.";
            }
        }

    }

    public void markOrderCompleted(String restaurantName, String orderId) {
        Restaurant restaurant = restaurants.get(restaurantName);
        if (restaurant == null) {
            throw new IllegalArgumentException("Restaurant not found: " + restaurantName);
        }

        Order order = orders.get(orderId);
        if (order == null) {
            throw new IllegalArgumentException("Order not found: " + orderId);
        }

        synchronized (restaurant) {
            if (!restaurant.getProcessingOrders().contains(orderId)) {
                throw new IllegalStateException("Restaurant " + restaurantName + " is not processing order " + orderId);
            }
             restaurant.getProcessingOrders().remove(orderId);
             order.setStatus(Order.OrderStatus.COMPLETED);
        }

    }

    public void updateRestaurantMenu(String restaurantName, String itemName, double newPrice) {
        Restaurant restaurant = restaurants.get(restaurantName);
        if (restaurant == null) {
            throw new IllegalArgumentException("Restaurant not found: " + restaurantName);
        }
        synchronized (restaurant) {
            restaurant.getMenu().put(itemName, newPrice);
        }
    }

    private List<Restaurant> findRestaurantsThatCanFulfillOrder(Order order) {
        List<Restaurant> fulfillingRestaurants = new ArrayList<>();
        for (Restaurant restaurant : restaurants.values()) {
            boolean canFulfill = true;
            for (String itemName : order.getItems().keySet()) {
                if (!restaurant.getMenu().containsKey(itemName)) {
                    canFulfill = false;
                    break;
                }
            }
            if (canFulfill) {
                fulfillingRestaurants.add(restaurant);
            }
        }
        return fulfillingRestaurants;
    }

    // Entity classes
