import java.util.HashMap;
import java.util.LinkedHashSet;
import java.util.Map;

/**
 * Implements both LRU (Least Recently Used) and LFU (Least Frequently Used) caches.
 * Supports GET and PUT operations with eviction based on the chosen policy.
 */
public class CacheSystem {

    /**
     * LRU Cache Implementation
     */
    static class LRUCache {

        private final int capacity;
        private final Map<Integer, Integer> cache;
        private final LinkedHashSet<Integer> lruQueue;  // Maintain order of access

        /**
         * Constructor for LRU Cache.
         *
         * @param capacity The maximum number of key-value pairs the cache can hold.
         */
        public LRUCache(int capacity) {
            this.capacity = capacity;
            this.cache = new HashMap<>();
            this.lruQueue = new LinkedHashSet<>();
        }

        /**
         * Retrieves the value associated with the given key from the cache.
         *
         * @param key The key to retrieve the value for.
         * @return The value associated with the key if it exists, otherwise -1.
         * Time Complexity: O(1)
         */
        public int get(int key) {
            if (cache.containsKey(key)) {
                // Move the key to the end of the queue (most recently used)
                lruQueue.remove(key);
                lruQueue.add(key);
                return cache.get(key);
            }
            return -1;
        }

        /**
         * Inserts a new key-value pair into the cache.  If the cache is full, the least recently used
         * entry is evicted.
         *
         * @param key   The key to insert.
         * @param value The value to associate with the key.
         * Time Complexity: O(1)
         */
        public void put(int key, int value) {
            if (cache.containsKey(key)) {
                // Update the value and move the key to the end of the queue
                cache.put(key, value);
                lruQueue.remove(key);
                lruQueue.add(key);
            } else {
                if (cache.size() == capacity) {
                    // Evict the least recently used entry (first element in the queue)
                    int lruKey = lruQueue.iterator().next();
                    cache.remove(lruKey);
                    lruQueue.remove(lruKey);
                }
                // Insert the new key-value pair
                cache.put(key, value);
                lruQueue.add(key);
            }
        }
    }

    /**
     * LFU Cache Implementation
     */
    static class LFUCache {
        private final int capacity;
        private final Map<Integer, Integer> cache; // key to value
        private final Map<Integer, Integer> counts; // key to frequency
        private final Map<Integer, LinkedHashSet<Integer>> freqMap; // frequency to keys
        private int minFreq;

        /**
         * Constructor for LFU Cache.
         *
         * @param capacity The maximum number of key-value pairs the cache can hold.
         */
        public LFUCache(int capacity) {
            this.capacity = capacity;
            this.cache = new HashMap<>();
            this.counts = new HashMap<>();
            this.freqMap = new HashMap<>();
            this.minFreq = 1;
            freqMap.put(1, new LinkedHashSet<>()); // initialize freqMap with initial frequency
        }

        /**
         * Retrieves the value associated with the given key from the cache.
         *
         * @param key The key to retrieve the value for.
         * @return The value associated with the key if it exists, otherwise -1.
         * Time Complexity: O(1)
         */
        public int get(int key) {
            if (!cache.containsKey(key)) {
                return -1;
            }

            int freq = counts.get(key);
            freqMap.get(freq).remove(key);

            if (freq == minFreq && freqMap.get(freq).isEmpty()) {
                minFreq++;
            }

            counts.put(key, freq + 1);

            if (!freqMap.containsKey(freq + 1)) {
                freqMap.put(freq + 1, new LinkedHashSet<>());
            }
            freqMap.get(freq + 1).add(key);

            return cache.get(key);
        }

        /**
         * Inserts a new key-value pair into the cache.  If the cache is full, the least frequently used
         * entry is evicted.
         *
         * @param key   The key to insert.
         * @param value The value to associate with the key.
         * Time Complexity: O(1)
         */
        public void put(int key, int value) {
            if (capacity <= 0) {
                return;
            }

            if (cache.containsKey(key)) {
                cache.put(key, value);
                get(key); // Update frequency
                return;
            }

            if (cache.size() == capacity) {
                evict();
            }

            cache.put(key, value);
            counts.put(key, 1);
            minFreq = 1;
            freqMap.get(1).add(key);
        }

        /**
         * Evicts the least frequently used key.
         * Time Complexity: O(1)
         */
        private void evict() {
            int evicted = freqMap.get(minFreq).iterator().next();
            freqMap.get(minFreq).remove(evicted);
            cache.remove(evicted);
            counts.remove(evicted);

            if (freqMap.get(minFreq).isEmpty()) {
                // Reset minFreq if empty.  This optimization isn't strictly necessary
                // since minFreq will get adjusted in `get`, but it makes the logic cleaner.
                 if (cache.isEmpty()) {
                     minFreq = 1;
                 } else {
                    // Find next smallest frequency.  Important if the emptied frequency wasn't the smallest.
                    int newMinFreq = Integer.MAX_VALUE;
                    for (int freq : freqMap.keySet()) {
                        if (!freqMap.get(freq).isEmpty() && freq < newMinFreq) {
                            newMinFreq = freq;
                        }
                    }
                    if (newMinFreq == Integer.MAX_VALUE) {
                        minFreq = 1;  // all keys are empty
                    } else {
                        minFreq = newMinFreq;
                    }
                 }
            }
        }
    }

    /**
     * Main method to demonstrate the LRU and LFU cache implementations.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // LRU Test Cases
        System.out.println("LRU Cache Test Cases:");
        LRUCache lruCache = new LRUCache(2);
        lruCache.put(1, 10);
        lruCache.put(2, 20);
        System.out.println("GET 1: " + lruCache.get(1)); // Output: 10
        lruCache.put(3, 30);
        System.out.println("GET 2: " + lruCache.get(2)); // Output: -1
        System.out.println("GET 3: " + lruCache.get(3)); // Output: 30

        System.out.println("\nLRU Cache Edge Cases:");
        LRUCache lruCache2 = new LRUCache(1);
        lruCache2.put(1, 1);
        System.out.println("GET 1: " + lruCache2.get(1)); // Output: 1
        lruCache2.put(2, 2);
        System.out.println("GET 1: " + lruCache2.get(1)); // Output: -1
        System.out.println("GET 2: " + lruCache2.get(2)); // Output: 2

        LRUCache lruCache3 = new LRUCache(0);
        lruCache3.put(1,1);
        System.out.println("GET 1: " + lruCache3.get(1)); // Output: -1, because capacity = 0
        lruCache3.put(2,2);
        System.out.println("GET 2: " + lruCache3.get(2)); // Output: -1, because capacity = 0


        // LFU Test Cases
        System.out.println("\nLFU Cache Test Cases:");
        LFUCache lfuCache = new LFUCache(2);
        lfuCache.put(1, 5);
        lfuCache.put(2, 6);
        System.out.println("GET 1: " + lfuCache.get(1)); // Output: 5
        System.out.println("GET 1: " + lfuCache.get(1)); // Output: 5
        lfuCache.put(3, 7);
        System.out.println("GET 2: " + lfuCache.get(2)); // Output: -1
        System.out.println("GET 3: " + lfuCache.get(3)); // Output: 7

        System.out.println("\nLFU Cache Edge Cases:");
        LFUCache lfuCache2 = new LFUCache(0);
        lfuCache2.put(0, 0);
        System.out.println("GET 0: " + lfuCache2.get(0));  // Output: -1

        LFUCache lfuCache3 = new LFUCache(2);
        lfuCache3.put(2, 2);
        lfuCache3.put(1, 1);
        lfuCache3.get(2);
        lfuCache3.get(1);
        lfuCache3.get(2);
        lfuCache3.put(3, 3);
        lfuCache3.put(4, 4);
        System.out.println("GET 3: " + lfuCache3.get(3)); // Output: -1
        System.out.println("GET 2: " + lfuCache3.get(2)); // Output: 2
        System.out.println("GET 1: " + lfuCache3.get(1)); // Output: 1
        System.out.println("GET 4: " + lfuCache3.get(4)); // Output: -1


        LFUCache lfuCache4 = new LFUCache(1);
        lfuCache4.put(2,1);
        System.out.println("GET 2: " + lfuCache4.get(2)); // Output: 1
        lfuCache4.put(3,2);
        System.out.println("GET 2: " + lfuCache4.get(2)); // Output: -1
        System.out.println("GET 3: " + lfuCache4.get(3)); // Output: 2

        LFUCache lfuCache5 = new LFUCache(3);
