import java.util.*;

public class AnagramPartition {

    /**
     * Partitions an array of strings into disjoint sets of anagrams.
     *
     * @param strings The input array of strings.
     * @return A list of disjoint sets (each set being a list of strings) such that
     *         strings in each set are anagrams of each other.
     *
     * Time Complexity: O(N * KlogK), where N is the number of strings and K is the maximum length of a string.
     * Space Complexity: O(N * K), where N is the number of strings and K is the maximum length of a string.
     */
    public List<List<String>> partitionAnagrams(String[] strings) {
        // Use a map to group anagrams by their sorted form.
        Map<String, List<String>> anagramGroups = new HashMap<>();

        // Iterate through each string in the input array.
        for (String str : strings) {
            // Sort the characters of the string to create a unique key for anagrams.
            char[] charArray = str.toCharArray();
            Arrays.sort(charArray);
            String sortedString = new String(charArray);

            // If the sorted string is already a key in the map, add the current string to the list.
            // Otherwise, create a new list with the current string and add it to the map with the sorted string as the key.
            anagramGroups.computeIfAbsent(sortedString, k -> new ArrayList<>()).add(str);
        }

        // Convert the map values (lists of anagrams) to a list of lists.
        return new ArrayList<>(anagramGroups.values());
    }

    public static void main(String[] args) {
        AnagramPartition anagramPartition = new AnagramPartition();

        // Test case 1
        String[] strings1 = {"eat", "tea", "tan", "ate", "nat", "bat"};
        List<List<String>> result1 = anagramPartition.partitionAnagrams(strings1);
        System.out.println("Test Case 1: " + result1); // Expected: [["eat", "tea", "ate"], ["tan", "nat"], ["bat"]]

        // Test case 2
        String[] strings2 = {"abc", "bca", "xyz", "zyx"};
        List<List<String>> result2 = anagramPartition.partitionAnagrams(strings2);
        System.out.println("Test Case 2: " + result2); // Expected: [["abc", "bca"], ["xyz", "zyx"]]

        // Test case 3 (empty array)
        String[] strings3 = {};
        List<List<String>> result3 = anagramPartition.partitionAnagrams(strings3);
        System.out.println("Test Case 3: " + result3); // Expected: []

        // Test case 4 (array with one element)
        String[] strings4 = {"abc"};
        List<List<String>> result4 = anagramPartition.partitionAnagrams(strings4);
        System.out.println("Test Case 4: " + result4); // Expected: [["abc"]]

        // Test case 5 (array with all identical strings)
        String[] strings5 = {"abc", "abc", "abc"};
        List<List<String>> result5 = anagramPartition.partitionAnagrams(strings5);
        System.out.println("Test Case 5: " + result5); // Expected: [["abc", "abc", "abc"]]

        // Test case 6 (array with duplicate anagrams and non-anagrams)
        String[] strings6 = {"eat", "tea", "ate", "eat", "bat", "tab"};
        List<List<String>> result6 = anagramPartition.partitionAnagrams(strings6);
        System.out.println("Test Case 6: " + result6); // Expected: [["eat", "tea", "ate", "eat"], ["bat", "tab"]]

        // Test case 7 (Long strings)
        String[] strings7 = {"aaaaaaaaab", "baaaaaaaaa", "bbbbbbbbbb"};
        List<List<String>> result7 = anagramPartition.partitionAnagrams(strings7);
        System.out.println("Test Case 7: " + result7);
    }
}
