public class BinaryTreeToLinkedList {

    // TreeNode class representing a node in the binary tree
    static class TreeNode {
        int val;
        TreeNode left;
        TreeNode right;

        TreeNode(int val) {
            this.val = val;
            this.left = null;
            this.right = null;
        }
    }

    /**
     * Converts a binary tree into a linked list (flattened tree) using pre-order traversal.
     * The 'right' pointer of each node is used as the 'next' pointer in the linked list,
     * and the 'left' pointer is set to null.
     *
     * @param root The root of the binary tree.
     */
    public static void flatten(TreeNode root) {
        if (root == null) {
            return;
        }

        // 1. Flatten the left subtree
        flatten(root.left);

        // 2. Flatten the right subtree
        flatten(root.right);

        // 3. Store the left and right subtrees
        TreeNode leftTree = root.left;
        TreeNode rightTree = root.right;

        // 4. Make the left subtree the right subtree
        root.left = null;  // Set left pointer to null as required
        root.right = leftTree;

        // 5. Traverse to the end of the (new) right subtree (formerly the left subtree)
        TreeNode current = root;
        while (current.right != null) {
            current = current.right;
        }

        // 6. Attach the original right subtree to the end of the new right subtree
        current.right = rightTree;
    }


    // Helper function to print the linked list representation of the tree (for testing)
    public static void printLinkedList(TreeNode root) {
        TreeNode current = root;
        while (current != null) {
            System.out.print(current.val + " ");
            current = current.right;
        }
        System.out.println();
    }


    public static void main(String[] args) {
