import java.util.LinkedList;
import java.util.Queue;

// Definition for a binary tree node.
class TreeNode {
    int data;
    TreeNode left;
    TreeNode right;

    TreeNode(int data) {
        this.data = data;
        this.left = null;
        this.right = null;
    }
}

public class LeftViewBinaryTree {

    /**
     * Prints the left view of a binary tree. The left view is the set of nodes
     * visible when the tree is viewed from the left side, printed from top to bottom.
     *
     * @param root The root of the binary tree.
     */
    public static void printLeftView(TreeNode root) {
        if (root == null) {
            return; // Handle empty tree case
        }

        Queue<TreeNode> queue = new LinkedList<>();
        queue.offer(root);

        while (!queue.isEmpty()) {
            int size = queue.size(); // Number of nodes at the current level
            for (int i = 0; i < size; i++) {
                TreeNode current = queue.poll();

                // Print the leftmost node at each level
                if (i == 0) {
                    System.out.print(current.data + (queue.isEmpty() && i == size -1 ? "" : ",")); // Print comma unless last node
                }

                // Add children to the queue for the next level
                if (current.left != null) {
                    queue.offer(current.left);
                }
                if (current.right != null) {
                    queue.offer(current.right);
                }
            }
        }
    }

    /**
     * Main method for testing the left view printing.  Includes several test cases
     * to demonstrate the functionality and handle different tree structures, including
     * empty trees, skewed trees, and complete binary trees.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test Case 1: Example Tree
        TreeNode root1 = new TreeNode(1);
        root1.left = new TreeNode(8);
        root1.right = new TreeNode(2);
        root1.right.left = new TreeNode(3);
