        root3.left = new TreeNode(2);
        root3.left.left = new TreeNode(3);
        root3.left.left.left = new TreeNode(4);
        System.out.print("Left View of Test Case 3 (Skewed Left): ");
        printLeftView(root3);
        System.out.println();

        // Test Case 4: Skewed Right Tree
        TreeNode root4 = new TreeNode(1);
        root4.right = new TreeNode(2);
        root4.right.right = new TreeNode(3);
        root4.right.right.right = new TreeNode(4);
        System.out.print("Left View of Test Case 4 (Skewed Right): ");
