import java.util.LinkedList;
import java.util.Queue;

class TreeNode {
    int val;
    TreeNode left;
    TreeNode right;

    TreeNode(int val) {
        this.val = val;
    }
}

public class TripletSumBinaryTree {

    /**
     * Counts the number of triplets (grandparent -> parent -> child) in a binary tree
     * whose sum equals the target value N.
     *
     * @param root The root of the binary tree.
     * @param N    The target sum.
     * @return The number of triplets that sum up to N.
     */
    public int countTriplets(TreeNode root, int N) {
        if (root == null) {
            return 0;
        }

        return countTripletsHelper(root, N);
    }

    private int countTripletsHelper(TreeNode root, int N) {
        int count = 0;
        Queue<TreeNode> queue = new LinkedList<>();
        queue.offer(root);

        while (!queue.isEmpty()) {
            TreeNode grandparent = queue.poll();

            if (grandparent.left != null) {
                TreeNode parentLeft = grandparent.left;
                queue.offer(parentLeft); // Add left child for level order traversal

                // Check for children of the parentLeft (grandchildren)
                if (parentLeft.left != null && grandparent.val + parentLeft.val + parentLeft.left.val == N) {
                    count++;
                }
                if (parentLeft.right != null && grandparent.val + parentLeft.val + parentLeft.right.val == N) {
                    count++;
                }
            }

            if (grandparent.right != null) {
                TreeNode parentRight = grandparent.right;
                queue.offer(parentRight); // Add right child for level order traversal


                // Check for children of the parentRight (grandchildren)
                if (parentRight.left != null && grandparent.val + parentRight.val + parentRight.left.val == N) {
                    count++;
                }
                if (parentRight.right != null && grandparent.val + parentRight.val + parentRight.right.val == N) {
                    count++;
                }
            }
        }

        return count;
    }


