        String str2 = "cba";
        int k2 = 2;
        System.out.println("Input: str = \"" + str2 + "\", k = " + k2);
        System.out.println("Output: " + processString(str2, k2)); // Expected: acb

        System.out.println("\nTest Case 3:");
        String str3 = "aabbcc";
        int k3 = 3;
        System.out.println("Input: str = \"" + str3 + "\", k = " + k3);
        System.out.println("Output: " + processString(str3, k3)); // Expected: aabcaabc

        System.out.println("\nTest Case 4 (Edge Case - Empty String):");
        String str4 = "";
        int k4 = 5;
        System.out.println("Input: str = \"" + str4 + "\", k = " + k4);
