public class EquilibriumIndex {

    /**
     * Finds the equilibrium index of an array.
     * An equilibrium index is an index such that the sum of elements at lower indexes is equal to the sum of elements at higher indexes.
     *
     * @param arr The input array of integers.
     * @return The equilibrium index if it exists, or -1 if no equilibrium index exists.
     *
     * Time Complexity: O(n), where n is the length of the array.  We iterate through the array twice.
     * Space Complexity: O(1). We only use a few constant extra variables.
     */
    public static int findEquilibriumIndex(int[] arr) {
        if (arr == null || arr.length == 0) {
            return -1; // Handle empty or null array.
        }

        int totalSum = 0;
        for (int num : arr) {
            totalSum += num;
        }

        int leftSum = 0;
        for (int i = 0; i < arr.length; i++) {
            // Calculate the right sum by subtracting the current element and the left sum from the total sum.
            int rightSum = totalSum - arr[i] - leftSum;

            if (leftSum == rightSum) {
                return i; // Found the equilibrium index.
            }

            leftSum += arr[i]; // Update the left sum for the next iteration.
        }

        return -1; // No equilibrium index found.
    }

    public static void main(String[] args) {
        // Test cases
        int[] arr1 = {-7, 1, 5, 2, -4, 3, 0};
        System.out.println("Equilibrium index of arr1: " + findEquilibriumIndex(arr1)); // Expected output: 3

        int[] arr2 = {2, 3, 4};
        System.out.println("Equilibrium index of arr2: " + findEquilibriumIndex(arr2)); // Expected output: -1

        int[] arr3 = {1, 2, 3, 4, 5};
        System.out.println("Equilibrium index of arr3: " + findEquilibriumIndex(arr3)); // Expected output: -1

        int[] arr4 = {0, 0, 0, 0};
        System.out.println("Equilibrium index of arr4: " + findEquilibriumIndex(arr4)); // Expected output: 0

        int[] arr5 = {-1, 0, 1};
        System.out.println("Equilibrium index of arr5: " + findEquilibriumIndex(arr5)); // Expected output: 1

        int[] arr6 = {1};
        System.out.println("Equilibrium index of arr6: " + findEquilibriumIndex(arr6)); // Expected output: 0

        int[] arr7 = {}; // Empty array test case
        System.out.println("Equilibrium index of arr7: " + findEquilibriumIndex(arr7)); // Expected output: -1

        int[] arr8 = { -7, 1, 5, 2, -4, 3 }; // Test case where equilibrium index is at the end is absent
        System.out.println("Equilibrium index of arr8: " + findEquilibriumIndex(arr8)); //Expected Output: -1

        int[] arr9 = {5, 5, 5, 5};
        System.out.println("Equilibrium index of arr9: " + findEquilibriumIndex(arr9));  // Expected Output: -1

        int[] arr10 = {-2, -1, 0, 1, 2};
        System.out.println("Equilibrium index of arr10: " + findEquilibriumIndex(arr10)); //Expected output: 2
    }
}
