import java.util.HashMap;
import java.util.Map;

/**
 * Solution for finding the most frequent element in an array.
 * If multiple elements have the same maximum frequency, returns any one of them.
 */
public class MostFrequentElement {

    /**
     * Finds the most frequent element in an array.
     *
     * @param arr The input array.
     * @return The most frequent element in the array. Returns -1 if the array is null or empty.
     */
    public static int findMostFrequentElement(int[] arr) {
        // Edge case: Handle null or empty array
        if (arr == null || arr.length == 0) {
            return -1; // Or throw an exception, depending on the desired behavior
        }

        // Use a HashMap to store the frequency of each element.
        Map<Integer, Integer> frequencyMap = new HashMap<>();

        // Iterate through the array and update the frequency map.
        for (int element : arr) {
            frequencyMap.put(element, frequencyMap.getOrDefault(element, 0) + 1);
        }

        // Find the element with the maximum frequency.
        int mostFrequentElement = arr[0]; // Initialize with the first element
        int maxFrequency = 0;

        for (Map.Entry<Integer, Integer> entry : frequencyMap.entrySet()) {
            if (entry.getValue() > maxFrequency) {
                maxFrequency = entry.getValue();
                mostFrequentElement = entry.getKey();
            }
        }

        return mostFrequentElement;
    }

    /**
     * Main method for testing the solution with various test cases.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        // Test case 1: Basic test case
        int[] arr1 = {1, 2, 2, 3, 3, 3, 4, 4, 4, 4};
