import java.util.Arrays;

public class DockyardPlacement {

    /**
     * Finds the optimal positions for two new dockyards to minimize the maximum distance
     * to the nearest dockyard.
     *
     * @param arr An array of integers representing the locations of existing dockyards.
     * @return An array containing two numbers, representing the locations for the two new dockyards.
     */
    public static double[] findOptimalDockyardPositions(int[] arr) {
        // 1. Sort the input array to easily find the gaps between dockyards.
        Arrays.sort(arr);

        // 2. Find the largest gap between existing dockyards.
        int largestGapIndex = -1;
        int largestGap = 0;
        for (int i = 0; i < arr.length - 1; i++) {
            int currentGap = arr[i + 1] - arr[i];
            if (currentGap > largestGap) {
                largestGap = currentGap;
                largestGapIndex = i;
            }
        }

        // 3. Calculate the positions for the new dockyards within the largest gap, dividing it into three equal parts.
        double firstNewDockyard = (double) arr[largestGapIndex] + (double) largestGap / 3.0;
        double secondNewDockyard = (double) arr[largestGapIndex] + 2.0 * (double) largestGap / 3.0;

        return new double[]{firstNewDockyard, secondNewDockyard};
    }

    /**
     * Main method for testing the solution with various test cases.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
