        int[] arr1 = {2, 10, 4};
        double[] result1 = findOptimalDockyardPositions(arr1);
        System.out.println("Test Case 1: arr = " + Arrays.toString(arr1) + ", Result = " + Arrays.toString(result1)); // Expected: [6.0, 8.0]

        // Test Case 2
