public class ConstructBSTFromPreorder {

    /**
     * Represents a node in the Binary Search Tree.
     */
    static class TreeNode {
        int val;
        TreeNode left;
        TreeNode right;

        TreeNode(int val) {
            this.val = val;
        }
    }

    /**
     * Constructs a Binary Search Tree from a given preorder traversal array.
     *
     * @param preorder The array representing the preorder traversal of the BST.
     * @return The root of the constructed BST.
     */
    public TreeNode bstFromPreorder(int[] preorder) {
        return bstFromPreorderHelper(preorder, new int[]{0}, Integer.MAX_VALUE, Integer.MIN_VALUE);
    }

    /**
     * Recursive helper function to construct the BST.
     *
     * @param preorder The array representing the preorder traversal of the BST.
     * @param index    An array to keep track of the current index in the preorder array.
     * @param upper    The upper bound for the current node's value (inherited from parent's right subtree).
     * @param lower    The lower bound for the current node's value (inherited from parent's left subtree).
     * @return The root of the constructed BST (or subtree).
     */
    private TreeNode bstFromPreorderHelper(int[] preorder, int[] index, int upper, int lower) {
        if (index[0] >= preorder.length) {
            return null; // Base case: No more elements in preorder array
        }

        int val = preorder[index[0]];

        // Check if the value is within the allowed range. If not, it doesn't belong to this subtree
        if (val < lower || val > upper) {
            return null;
        }

        TreeNode node = new TreeNode(val);
        index[0]++; // Increment the index to the next element in the preorder array

        // Recursively construct the left subtree with the updated upper bound.
        node.left = bstFromPreorderHelper(preorder, index, val, lower);

        // Recursively construct the right subtree with the updated lower bound.
        node.right = bstFromPreorderHelper(preorder, index, upper, val);

        return node;
    }

    /**
     * Helper method to convert a BST to an array representation for easy testing.
     * (Level order traversal with nulls).
     *
     * @param root The root of the BST.
     * @return An array representing the BST in level order with nulls.
     */
    public Integer[] treeToArray(TreeNode root) {
        if (root == null) {
            return new Integer[0]; // Handle empty tree case
        }

        java.util.List<Integer> result = new java.util.ArrayList<>();
        java.util.Queue<TreeNode> queue = new java.util.LinkedList<>();
        queue.offer(root);

        while (!queue.isEmpty()) {
            TreeNode node = queue.poll();
            if (node != null) {
                result.add(node.val);
                queue.offer(node.left);
                queue.offer(node.right);
            } else {
                result.add(null);
            }
        }

        // Remove trailing nulls from the end
        while (result.size() > 0 && result.get(result.size() - 1) == null) {
            result.remove(result.size() - 1);
        }

        return result.toArray(new Integer[0]);
    }


    public static void main(String[] args) {
        ConstructBSTFromPreorder constructor = new ConstructBSTFromPreorder();

        // Test case 1
        int[] preorder1 = {8, 5, 1, 7, 10, 12};
        TreeNode root1 = constructor.bstFromPreorder(preorder1);
        Integer[] result1 = constructor.treeToArray(root1);
        System.out.println("Test Case 1: " + java.util.Arrays.toString(result1)); // Expected output: [8, 5, 10, 1, 7, 12]

        // Test case 2
        int[] preorder2 = {1, 3};
        TreeNode root2 = constructor.bstFromPreorder(preorder2);
        Integer[] result2 = constructor.treeToArray(root2);
        System.out.println("Test Case 2: " + java.util.Arrays.toString(result2)); // Expected output: [1, null, 3]

        // Test case 3: Single node
        int[] preorder3 = {5};
        TreeNode root3 = constructor.bstFromPreorder(preorder3);
        Integer[] result3 = constructor.treeToArray(root3);
        System.out.println("Test Case 3: " + java.util.Arrays.toString(result3)); // Expected output: [5]

        // Test case 4: All left children
        int[] preorder4 = {5, 4, 3, 2, 1};
        TreeNode root4 = constructor.bstFromPreorder(preorder4);
        Integer[] result4 = constructor.treeToArray(root4);
        System.out.println("Test Case 4: " + java.util.Arrays.toString(result4)); // Expected output: [5, 4, null, 3, null, null, null, 2, null, null, null, 1]

        // Test case 5: All right children
        int[] preorder5 = {1, 2, 3, 4, 5};
        TreeNode root5 = constructor.bstFromPreorder(preorder5);
        Integer[] result5 = constructor.treeToArray(root5);
        System.out.println("Test Case 5: " + java.util.Arrays.toString(result5)); // Expected output: [1, null, 2, null, null, null, 3, null, null, null, null, null, null, null, 4, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, 5]


        // Test case 6: Empty preorder array
        int[] preorder6 = {};
        TreeNode root6 = constructor.bstFromPreorder(preorder6);
        Integer[] result6 = constructor.treeToArray(root6);
        System.out.println("Test Case 6: " + java.util.Arrays.toString(result6)); // Expected output: []

        //Test case 7 : example [1,5,3]
        int[] preorder7 = {1,5,3};
        TreeNode root7 = constructor.bstFromPreorder(preorder7);
        Integer[] result7 = constructor.treeToArray(root7);
        System.out.println("Test Case 7: " + java.util.Arrays.toString(result7)); // Expected output: [1, null, 5, 3]

    }

    /*
     * Time Complexity: O(N) where N is the number of nodes in the preorder array.
     *                  Each node is visited and processed exactly once.
     *
     * Space Complexity: O(H)  where H is the height of the BST.
     *                   In the worst-case scenario (skewed tree), H can be equal to N, resulting in O(N) space complexity.
     *                   In the best-case scenario (balanced tree), H would be log(N), resulting in O(log N) space complexity.
     *                   The auxillary space is mainly used by the recursion stack and  is directly proportional to the height.
     */
}
