import java.util.HashMap;
import java.util.Map;

public class PairSumCounter {

    /**
     * Counts the number of pairs in an array that sum up to a given target value.
     *
     * @param arr The input integer array.
     * @param sum The target sum.
     * @return The number of pairs in the array that sum up to the target value.
     *
     * Time Complexity: O(N), where N is the length of the array.  We iterate through the array once.
     * Space Complexity: O(N), in the worst-case scenario where all array elements are distinct,
     * the HashMap might store all N elements.  In the best case, the space complexity will be O(K), where K is the number of distinct elements
     */
    public static int countPairsWithSum(int[] arr, int sum) {
        if (arr == null || arr.length < 2) {
            return 0; // Handle edge cases: null or empty array
        }

        Map<Integer, Integer> frequencyMap = new HashMap<>();
        int pairCount = 0;

        for (int num : arr) {
            int complement = sum - num;
            if (frequencyMap.containsKey(complement)) {
                pairCount += frequencyMap.get(complement); // Increment pair count by the frequency of the complement
            }
            frequencyMap.put(num, frequencyMap.getOrDefault(num, 0) + 1); // Update frequency of the current number
        }

        return pairCount;
    }

    public static void main(String[] args) {
        // Test cases
        int[] arr1 = {1, 5, 7, -1, 5};
        int sum1 = 6;
