import java.util.Arrays;
import java.util.HashSet;
import java.util.Set;

public class TwoSum {

    /**
     * Checks if there exists a pair of integers in the given array whose sum is equal to the target value X.
     *
     * @param arr The input array of integers.
     * @param target The target sum to check for.
     * @return True if a pair exists with the sum equal to the target, false otherwise.
     *
     * Time Complexity: O(N), where N is the length of the input array.  We iterate through the array once.
     * Space Complexity: O(N), where N is the length of the input array.  The HashSet can potentially store all elements of the array.
     */
    public static boolean hasPairWithSum(int[] arr, int target) {
        if (arr == null || arr.length < 2) {
            return false; // Not enough elements to form a pair
        }

        Set<Integer> seen = new HashSet<>();
        for (int num : arr) {
            int complement = target - num;
            if (seen.contains(complement)) {
                return true; // Found a pair that sums up to the target
            }
            seen.add(num);
        }

        return false; // No pair found
    }

    /**
     * Returns the pair of integers in the given array whose sum is equal to the target value X.
     * If no such pair exists, it returns null.
     *
     * @param arr The input array of integers.
     * @param target The target sum to check for.
     * @return An array of two integers representing the pair that sums up to the target, or null if no such pair exists.
     *
     * Time Complexity: O(N), where N is the length of the input array. We iterate through the array once.
     * Space Complexity: O(N), where N is the length of the input array. The HashSet can potentially store all elements of the array.
     */
    public static int[] findPairWithSum(int[] arr, int target) {
        if (arr == null || arr.length < 2) {
            return null; // Not enough elements to form a pair
        }

        Set<Integer> seen = new HashSet<>();
        for (int num : arr) {
            int complement = target - num;
            if (seen.contains(complement)) {
                return new int[]{num, complement}; // Found a pair that sums up to the target
            }
            seen.add(num);
        }

        return null; // No pair found
    }

