public class ReverseAlternateKNodes {

    /**
     * Reverses every alternate N nodes in a linked list.
     *
     * @param head The head of the linked list.
     * @param n    The number of nodes to reverse alternately.
     * @return The head of the modified linked list.
     *
     * Time Complexity: O(n) - where n is the number of nodes in the linked list.  We iterate through the list once.
     * Space Complexity: O(1) - We are only using a constant amount of extra space.
     */
    public static ListNode reverseAlternateKNodes(ListNode head, int n) {
        if (head == null || n <= 0) {
            return head; // Handle edge cases: empty list or invalid N
        }

        ListNode current = head;
        ListNode prev = null;
        ListNode next = null;
        ListNode newHead = null; // The new head of the linked list after the first reversal
        boolean reverse = true;  // Flag to indicate whether to reverse the next group of N nodes

        while (current != null) {
            ListNode groupStart = current; // Mark the start of the current group
            ListNode prevOfGroupStart = prev; //  The previous node of the group start

            // Reverse the next N nodes if 'reverse' is true
            if (reverse) {
                int count = 0;
                prev = null;
                next = null;

                while (current != null && count < n) {
                    next = current.next;
                    current.next = prev;
                    prev = current;
                    current = next;
                    count++;
                }

                // 'prev' is now the head of the reversed group
                if (newHead == null) {
                    newHead = prev; // Assign the new head only once
                } else {
                    prevOfGroupStart.next = prev; // Link the previous group to the reversed group
                }

                groupStart.next = current; // Link the end of the reversed group to the remaining list
                prev = groupStart; // Set prev to the end of the reversed group (groupStart) for next iteration

                reverse = false; // Skip the next N nodes
            } else {
                int count = 0;
                while (current != null && count < n) {
                    prev = current; // Keep track of the last visited node
                    current = current.next;
                    count++;
                }
                reverse = true; // Reverse the next N nodes
            }
        }

        return newHead == null ? head : newHead; // If no reversal happened, return the original head
    }


    // Helper class for the linked list node
    public static class ListNode {
        int val;
        ListNode next;

        ListNode(int val) {
            this.val = val;
        }

        @Override
        public String toString() {
            return val + (next != null ? " -> " + next.toString() : "");
        }
    }

    public static void main(String[] args) {
        // Test case 1
        ListNode head1 = new ListNode(7);
        head1.next = new ListNode(8);
        head1.next.next = new ListNode(9);
        head1.next.next.next = new ListNode(10);
        head1.next.next.next.next = new ListNode(11);
        head1.next.next.next.next.next = new ListNode(12);
        head1.next.next.next.next.next.next = new ListNode(13);
        head1.next.next.next.next.next.next.next = new ListNode(14);
        head1.next.next.next.next.next.next.next.next = new ListNode(15);
        head1.next.next.next.next.next.next.next.next.next = new ListNode(16);

        System.out.println("Original List 1: " + head1);
        ListNode reversedHead1 = reverseAlternateKNodes(head1, 4);
        System.out.println("Reversed List 1: " + reversedHead1);

        // Test case 2: N > list size
        ListNode head2 = new ListNode(1);
        head2.next = new ListNode(2);
        head2.next.next = new ListNode(3);

        System.out.println("\nOriginal List 2: " + head2);
        ListNode reversedHead2 = reverseAlternateKNodes(head2, 5); // N > list size
        System.out.println("Reversed List 2: " + reversedHead2);

