        int[] arr2 = {2, 4, 6, 8, 10, 12};
        int target2 = 7;
        int[] result2 = finder.findFirstAndLastOccurrence(arr2, target2);
        System.out.println("Array: {2, 4, 6, 8, 10, 12}, Target: 7, Result: {" + result2[0] + ", " + result2[1] + "}");  // Expected: {-1, -1}

        int[] arr3 = {1, 2, 2, 2, 2, 3, 4, 5};
        int target3 = 2;
        int[] result3 = finder.findFirstAndLastOccurrence(arr3, target3);
        System.out.println("Array: {1, 2, 2, 2, 2, 3, 4, 5}, Target: 2, Result: {" + result3[0] + ", " + result3[1] + "}");  // Expected: {1, 4}

        int[] arr4 = {1, 2, 3, 4, 5};
        int target4 = 5;
        int[] result4 = finder.findFirstAndLastOccurrence(arr4, target4);
        System.out.println("Array: {1, 2, 3, 4, 5}, Target: 5, Result: {" + result4[0] + ", " + result4[1] + "}");  // Expected: {4, 4}

        int[] arr5 = {5, 5, 5, 5, 5};
        int target5 = 5;
        int[] result5 = finder.findFirstAndLastOccurrence(arr5, target5);
        System.out.println("Array: {5, 5, 5, 5, 5}, Target: 5, Result: {" + result5[0] + ", " + result5[1] + "}");  // Expected: {0, 4}

        int[] arr6 = {1};
        int target6 = 1;
        int[] result6 = finder.findFirstAndLastOccurrence(arr6, target6);
        System.out.println("Array: {1}, Target: 1, Result: {" + result6[0] + ", " + result6[1] + "}");  // Expected: {0, 0}

        int[] arr7 = {1};
        int target7 = 2;
