public class IntegerOccurrenceSortedArray {

    /**
     * Finds the total occurrence of an integer 'i' in a sorted integer array 'arr'.
     *
     * @param arr The sorted integer array.
     * @param i   The integer to search for.
     * @return The number of times 'i' occurs in 'arr'.  Returns 0 if arr is null or empty.
     *         Returns 0 if i is not found.
     */
    public static int countOccurrences(int[] arr, int i) {
        if (arr == null || arr.length == 0) {
            return 0;
        }

        int firstOccurrence = findFirstOccurrence(arr, i);

        // If 'i' is not found in the array, return 0
        if (firstOccurrence == -1) {
            return 0;
        }

        int lastOccurrence = findLastOccurrence(arr, i);

        return lastOccurrence - firstOccurrence + 1;
    }

    /**
     * Finds the index of the first occurrence of 'i' in the sorted array 'arr' using binary search.
     *
     * @param arr The sorted integer array.
     * @param i   The integer to search for.
     * @return The index of the first occurrence of 'i', or -1 if 'i' is not found.
     */
    private static int findFirstOccurrence(int[] arr, int i) {
        int low = 0;
        int high = arr.length - 1;
        int firstOccurrence = -1;

        while (low <= high) {
            int mid = low + (high - low) / 2; // Prevent potential integer overflow

            if (arr[mid] == i) {
                firstOccurrence = mid;
                high = mid - 1; // Continue searching to the left for earlier occurrences
            } else if (arr[mid] < i) {
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }

        return firstOccurrence;
    }

    /**
     * Finds the index of the last occurrence of 'i' in the sorted array 'arr' using binary search.
     *
     * @param arr The sorted integer array.
     * @param i   The integer to search for.
     * @return The index of the last occurrence of 'i', or -1 if 'i' is not found.
     */
    private static int findLastOccurrence(int[] arr, int i) {
        int low = 0;
        int high = arr.length - 1;
        int lastOccurrence = -1;

        while (low <= high) {
            int mid = low + (high - low) / 2; // Prevent potential integer overflow

            if (arr[mid] == i) {
                lastOccurrence = mid;
                low = mid + 1; // Continue searching to the right for later occurrences
            } else if (arr[mid] < i) {
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }

        return lastOccurrence;
    }


    public static void main(String[] args) {
