import java.util.*;
import java.awt.Point;

public class ImageTagging {

    /**
     * Represents a tag within an image.
     */
    static class Tag {
        private final String id;
        private final Point location;
        private String description;
        private String link;
        private String category;

        public Tag(String id, Point location, String description, String link, String category) {
            this.id = id;
            this.location = location;
            this.description = description;
            this.link = link;
            this.category = category;
        }

        public String getId() {
            return id;
        }

        public Point getLocation() {
            return location;
        }

        public String getDescription() {
            return description;
        }

        public void setDescription(String description) {
            this.description = description;
        }

        public String getLink() {
            return link;
        }

        public void setLink(String link) {
            this.link = link;
        }

        public String getCategory() {
            return category;
        }

        public void setCategory(String category) {
            this.category = category;
        }

        @Override
        public String toString() {
            return "Tag{" +
                    "id='" + id + '\'' +
                    ", location=" + location +
                    ", description='" + description + '\'' +
                    ", link='" + link + '\'' +
                    ", category='" + category + '\'' +
                    '}';
        }
    }

    /**
     * Stores image data and its associated tags.  Uses a HashMap for quick tag lookup by ID.
     */
    static class Image {
        private final String imageId;
        private final String imageUrl;
        private final Map<String, Tag> tags;

        public Image(String imageId, String imageUrl) {
            this.imageId = imageId;
            this.imageUrl = imageUrl;
            this.tags = new HashMap<>();
        }

        public String getImageId() {
            return imageId;
        }

        public String getImageUrl() {
            return imageUrl;
        }

        public Map<String, Tag> getTags() {
            return tags;
        }

        /**
         * Adds a tag to the image.
         *
         * @param tag The tag to add.
         */
        public void addTag(Tag tag) {
            tags.put(tag.getId(), tag);
        }

        /**
         * Removes a tag from the image.
         *
         * @param tagId The ID of the tag to remove.
         */
        public void removeTag(String tagId) {
            tags.remove(tagId);
        }

        /**
         * Retrieves a tag by its ID.
         *
         * @param tagId The ID of the tag to retrieve.
         * @return The tag, or null if not found.
         */
        public Tag getTag(String tagId) {
            return tags.get(tagId);
        }
    }

    /**
     * Manages images and their tags. Simulates a simplified database or storage layer.
     * Uses a HashMap for fast image retrieval by ID.
     */
    static class ImageTaggingService {
        private final Map<String, Image> images;

        public ImageTaggingService() {
            this.images = new HashMap<>();
        }

        /**
         * Adds an image to the service.
         *
         * @param image The image to add.
         */
        public void addImage(Image image) {
            images.put(image.getImageId(), image);
        }

        /**
         * Retrieves an image by its ID.
         *
         * @param imageId The ID of the image to retrieve.
         * @return The image, or null if not found.
         */
        public Image getImage(String imageId) {
            return images.get(imageId);
        }

        /**
         * Adds a tag to an image.
         *
         * @param imageId The ID of the image to add the tag to.
         * @param tag     The tag to add.
         */
        public void addTagToImage(String imageId, Tag tag) {
            Image image = getImage(imageId);
            if (image != null) {
                image.addTag(tag);
            } else {
                System.out.println("Image with ID " + imageId + " not found.");
            }
        }

        /**
         * Removes a tag from an image.
         *
         * @param imageId The ID of the image to remove the tag from.
         * @param tagId   The ID of the tag to remove.
         */
        public void removeTagFromImage(String imageId, String tagId) {
            Image image = getImage(imageId);
            if (image != null) {
                image.removeTag(tagId);
            } else {
                System.out.println("Image with ID " + imageId + " not found.");
            }
        }

        /**
         * Retrieves all tags for a given image.
         * @param imageId The ID of the image.
         * @return A collection of tags for the image, or an empty collection if the image is not found.
         */
        public Collection<Tag> getAllTagsForImage(String imageId) {
            Image image = getImage(imageId);
            if (image != null) {
                return image.getTags().values();
            } else {
                System.out.println("Image with ID " + imageId + " not found.");
                return Collections.emptyList();
            }
        }

        /**
         * Finds tags near a given location on an image.  This simulates the "hover" functionality.
         *
         * @param imageId  The ID of the image.
         * @param location The location to check for nearby tags.
         * @param radius   The radius around the location to search for tags.
         * @return A list of tags within the given radius of the location.
         */
        public List<Tag> findTagsNearLocation(String imageId, Point location, double radius) {
            Image image = getImage(imageId);
            if (image == null) {
                System.out.println("Image with ID " + imageId + " not found.");
                return Collections.emptyList();
            }

            List<Tag> nearbyTags = new ArrayList<>();
            for (Tag tag : image.getTags().values()) {
                double distance = location.distance(tag.getLocation());
                if (distance <= radius) {
                    nearbyTags.add(tag);
                }
            }
            return nearbyTags;
        }
    }

    public static void main(String[] args) {
        // Initialize the ImageTaggingService
        ImageTaggingService service = new ImageTaggingService();

