import java.util.*;
import java.util.concurrent.*;
import java.util.stream.Collectors;

enum ResourceType {
    SERVER_INSTANCE
}

enum TaskStatus {
    QUEUED,
    RUNNING,
    COMPLETED,
    FAILED
}

class Resource {
    private final String id;
    private final ResourceType type;
    private final int cpu;
    private final double price; // Price per unit time
    private boolean isAvailable;

    public Resource(String id, ResourceType type, int cpu, double price) {
        this.id = id;
        this.type = type;
        this.cpu = cpu;
        this.price = price;
        this.isAvailable = true;
    }

    public String getId() {
        return id;
    }

    public ResourceType getType() {
        return type;
    }

    public int getCpu() {
        return cpu;
    }

    public double getPrice() {
        return price;
    }

    public boolean isAvailable() {
        return isAvailable;
    }

    public void setAvailable(boolean available) {
        isAvailable = available;
    }

    @Override
    public String toString() {
        return "Resource{" +
                "id='" + id + '\'' +
                ", type=" + type +
                ", cpu=" + cpu +
                ", price=" + price +
                ", isAvailable=" + isAvailable +
                '}';
    }
}

class Task {
    private final String id;
    private final ResourceType resourceType;
    private final int minCpu;
    private TaskStatus status;
    private Resource allocatedResource;
    private long startTime;
    private long endTime;

    public Task(String id, ResourceType resourceType, int minCpu) {
        this.id = id;
        this.resourceType = resourceType;
        this.minCpu = minCpu;
        this.status = TaskStatus.QUEUED;
    }

    public String getId() {
        return id;
    }

    public ResourceType getResourceType() {
        return resourceType;
    }

    public int getMinCpu() {
        return minCpu;
    }

    public TaskStatus getStatus() {
        return status;
    }

    public void setStatus(TaskStatus status) {
        this.status = status;
    }

    public Resource getAllocatedResource() {
        return allocatedResource;
    }

    public void setAllocatedResource(Resource allocatedResource) {
        this.allocatedResource = allocatedResource;
    }

    public long getStartTime() {
        return startTime;
    }

    public void setStartTime(long startTime) {
        this.startTime = startTime;
    }

    public long getEndTime() {
        return endTime;
    }

    public void setEndTime(long endTime) {
        this.endTime = endTime;
    }

    @Override
    public String toString() {
        return "Task{" +
                "id='" + id + '\'' +
                ", resourceType=" + resourceType +
                ", minCpu=" + minCpu +
                ", status=" + status +
                ", allocatedResource=" + allocatedResource +
                ", startTime=" + startTime +
                ", endTime=" + endTime +
                '}';
    }
}


class DataCenter {
    private final String name;
    private final List<Resource> resources;
    private final Queue<Task> taskQueue;
    private final ExecutorService executorService; // For concurrent task execution
    private final Map<String, Task> taskMap;

    public DataCenter(String name) {
        this.name = name;
        this.resources = new ArrayList<>();
        this.taskQueue = new LinkedList<>();
        this.executorService = Executors.newFixedThreadPool(10); // Adjust thread pool size as needed
        this.taskMap = new ConcurrentHashMap<>();
    }

    public String getName() {
        return name;
    }

    public void addResource(Resource resource) {
        resources.add(resource);
    }

    public void deleteResource(String resourceId) {
        resources.removeIf(resource -> resource.getId().equals(resourceId));
    }

    public List<Resource> getAvailableResources(ResourceType type, int minCpu) {
        return resources.stream()
                .filter(resource -> resource.getType() == type && resource.getCpu() >= minCpu && resource.isAvailable())
                .collect(Collectors.toList());
    }

    public List<Resource> getAllocatedResources(ResourceType type) {
        return resources.stream()
                .filter(resource -> resource.getType() == type && !resource.isAvailable())
                .collect(Collectors.toList());
    }

    // Criteria 1: Allocate the least price resource
    public boolean allocateResourceLeastPrice(Task task) {
        List<Resource> availableResources = getAvailableResources(task.getResourceType(), task.getMinCpu());
        if (availableResources.isEmpty()) {
            taskQueue.offer(task);
            taskMap.put(task.getId(), task);
            task.setStatus(TaskStatus.QUEUED);
            System.out.println("Task " + task.getId() + " queued, no resources available.");
            return false;
        }

        //Sort based on least price
        Resource bestResource = availableResources.stream()
                .min(Comparator.comparingDouble(Resource::getPrice))
                .orElse(null);

        if (bestResource != null) {
            return allocateAndExecuteTask(task, bestResource);
        } else {
            System.out.println("Error: No suitable resource found even though available resources exist.");
            return false; // or throw an exception
        }
    }

    // Criteria 2: Allocate the resource with best execution time (highest CPU)
    public boolean allocateResourceBestExecutionTime(Task task) {
        List<Resource> availableResources = getAvailableResources(task.getResourceType(), task.getMinCpu());
        if (availableResources.isEmpty()) {
            taskQueue.offer(task);
            taskMap.put(task.getId(), task);
            task.setStatus(TaskStatus.QUEUED);
            System.out.println("Task " + task.getId() + " queued, no resources available.");
            return false;
        }

        //Sort based on highest CPU
        Resource bestResource = availableResources.stream()
                .max(Comparator.comparingInt(Resource::getCpu))
                .orElse(null);

        if (bestResource != null) {
            return allocateAndExecuteTask(task, bestResource);
        } else {
            System.out.println("Error: No suitable resource found even though available resources exist.");
            return false; // or throw an exception
        }
    }

    private boolean allocateAndExecuteTask(Task task, Resource resource) {
        resource.setAvailable(false);
        task.setAllocatedResource(resource);
        task.setStatus(TaskStatus.RUNNING);
        task.setStartTime(System.currentTimeMillis());
        taskMap.put(task.getId(), task);
        System.out.println("Allocated resource " + resource.getId() + " to task " + task.getId());

        // Simulate task execution
        executorService.submit(() -> {
            try {
                // Simulate execution time based on task's CPU requirement and resource's CPU
                long executionTime = (long) (10000 * (double) task.getMinCpu() / resource.getCpu()); // Example: execution time is inversely proportional to CPU
                System.out.println("Task " + task.getId() + " executing on resource " + resource.getId() + " for " + executionTime + " ms");
                Thread.sleep(executionTime);
                System.out.println("Task " + task.getId() + " completed on resource " + resource.getId());
                task.setStatus(TaskStatus.COMPLETED);
                task.setEndTime(System.currentTimeMillis());
                taskMap.put(task.getId(), task);
            } catch (InterruptedException e) {
                System.err.println("Task " + task.getId() + " interrupted: " + e.getMessage());
                task.setStatus(TaskStatus.FAILED);
                taskMap.put(task.getId(), task);
            } finally {
                resource.setAvailable(true);
                System.out.println("Resource " + resource.getId() + " is now available.");
                processWaitingTasks();  // Check if there are any waiting tasks
            }
        });

        return true;
    }

    private void processWaitingTasks() {
        synchronized (taskQueue) {
            while (!taskQueue.isEmpty()) {
                Task task = taskQueue.peek();
                if (task == null) {
                    break;
                }
                List<Resource> availableResources = getAvailableResources(task.getResourceType(), task.getMinCpu());
                if (!availableResources.isEmpty()) {
                    taskQueue.poll(); // Remove the task from the queue
                    Resource bestResource = availableResources.stream()
                            .min(Comparator.comparingDouble(Resource::getPrice))
                            .orElse(null);
                    if(bestResource != null) {
                        allocateAndExecuteTask(task, bestResource);
                        System.out.println("Task " + task.getId() + " removed from waiting queue and allocated.");
                    } else {
                        taskQueue.offer(task); // Put back on the queue if no suitable resource is found.  Shouldn't happen, but safe.
                        break;
                    }

                } else {
                    break; // No resources available, stop processing the queue
                }
            }
        }
    }

    public TaskStatus getTaskStatus(String taskId) {
        Task task = taskMap.get(taskId);
        return task != null ? task.getStatus() : null;
    }

    public Map<String, Object> getTaskDetails(String taskId) {
       Task task = taskMap.get(taskId);
       if (task == null) {
           return null;
       }
       Map<String, Object> details = new HashMap<>();
       details.put("status", task.getStatus());
       details.put("resourceId", task.getAllocatedResource() != null ? task.getAllocatedResource().getId() : null);
       details.put("startTime", task.getStartTime());
       details.put("endTime", task.getEndTime());
       return details;
    }

    public void shutdown() {
        executorService.shutdown();
    }
}

