        //Edge case: Add resource after task is queued.
        dataCenter.addResource(new Resource("resource5", ResourceType.SERVER_INSTANCE, 12, 15.0));
        dataCenter.addResource(new Resource("resource6", ResourceType.SERVER_INSTANCE, 10, 16.0));


        Thread.sleep(10000); //wait some time for the queued tasks to pick up the resource

        System.out.println("Task4 status after completion: " + dataCenter.getTaskStatus("task4"));
        System.out.println("Task5 status after completion: " + dataCenter.getTaskStatus("task5"));

        // Clean up resources
        dataCenter.shutdown();

        //Demonstrates the get available resources with filtering and sorting
        DataCenter dataCenter2 = new DataCenter("SecondDataCenter");
        dataCenter2.addResource(new Resource("resource7", ResourceType.SERVER_INSTANCE, 8, 10.0));
        dataCenter2.addResource(new Resource("resource8", ResourceType.SERVER_INSTANCE, 16, 18.0));
        dataCenter2.addResource(new Resource("resource9", ResourceType.SERVER_INSTANCE, 8, 9.0));

        List<Resource> available8CpuResources = dataCenter2.getAvailableResources(ResourceType.SERVER_INSTANCE, 8);
        System.out.println("Available 8 CPU resources: " + available8CpuResources);
    }
