import java.util.HashMap;
import java.util.Map;

public class MinWindowSubstring {

    /**
     * Finds the smallest substring in source that contains all the characters present in target.
     *
     * @param source The source string.
     * @param target The target string.
     * @return The smallest substring in source that contains all the characters in target, or an empty string if no such substring exists.
     *
     * Time Complexity: O(n + m), where n is the length of the source string and m is the length of the target string.
     * Space Complexity: O(m), where m is the length of the target string (for storing character frequencies).
     */
    public String minWindow(String source, String target) {
        if (source == null || target == null || source.length() == 0 || target.length() == 0) {
            return "";
        }

        // Character frequency map for the target string
        Map<Character, Integer> targetMap = new HashMap<>();
        for (char c : target.toCharArray()) {
            targetMap.put(c, targetMap.getOrDefault(c, 0) + 1);
        }

        // Required number of characters to match
        int required = targetMap.size();

        // Left and right pointers for the sliding window
        int left = 0;
        int right = 0;

        // Number of matched characters in the current window
        int formed = 0;

        // Character frequency map for the current window
        Map<Character, Integer> windowMap = new HashMap<>();

        // Minimum window length and its start index
        int minLength = Integer.MAX_VALUE;
        int start = 0;

        while (right < source.length()) {
            // Add the current character to the window
            char c = source.charAt(right);
            windowMap.put(c, windowMap.getOrDefault(c, 0) + 1);

            // If the character is in the target and its frequency matches the target frequency, increment formed
            if (targetMap.containsKey(c) && windowMap.get(c).intValue() == targetMap.get(c).intValue()) {
                formed++;
            }

            // Try to contract the window while all characters are matched
            while (left <= right && formed == required) {
                c = source.charAt(left);

                // Save the smallest window found so far
                if (right - left + 1 < minLength) {
                    minLength = right - left + 1;
                    start = left;
                }

                // Remove the leftmost character from the window
                windowMap.put(c, windowMap.get(c) - 1);

                // If the removed character is in the target and its frequency is now less than the target frequency, decrement formed
                if (targetMap.containsKey(c) && windowMap.get(c).intValue() < targetMap.get(c).intValue()) {
                    formed--;
                }

                // Move the left pointer
                left++;
            }

            // Move the right pointer to expand the window
            right++;
        }

        // If no valid window found, return an empty string
        return minLength == Integer.MAX_VALUE ? "" : source.substring(start, start + minLength);
    }

    public static void main(String[] args) {
        MinWindowSubstring minWindowSubstring = new MinWindowSubstring();

        // Test cases
        String source1 = "this is a test string";
        String target1 = "tist";
        System.out.println("Source: " + source1 + ", Target: " + target1 + ", Result: " + minWindowSubstring.minWindow(source1, target1)); // Expected: "t stri"

        String source2 = "abca";
        String target2 = "ac";
        System.out.println("Source: " + source2 + ", Target: " + target2 + ", Result: " + minWindowSubstring.minWindow(source2, target2)); // Expected: "ca"

        String source3 = "abcdefg";
        String target3 = "xyz";
        System.out.println("Source: " + source3 + ", Target: " + target3 + ", Result: " + minWindowSubstring.minWindow(source3, target3)); // Expected: ""

        String source4 = "ADOBECODEBANC";
        String target4 = "ABC";
        System.out.println("Source: " + source4 + ", Target: " + target4 + ", Result: " + minWindowSubstring.minWindow(source4, target4)); // Expected: "BANC"

        String source5 = "a";
        String target5 = "a";
        System.out.println("Source: " + source5 + ", Target: " + target5 + ", Result: " + minWindowSubstring.minWindow(source5, target5)); // Expected: "a"

        String source6 = "a";
        String target6 = "aa";
        System.out.println("Source: " + source6 + ", Target: " + target6 + ", Result: " + minWindowSubstring.minWindow(source6, target6)); // Expected: ""

        String source7 = "aaaaaaaaaaaabbbbbcddddddddddddd";
        String target7 = "abcdd";
        System.out.println("Source: " + source7 + ", Target: " + target7 + ", Result: " + minWindowSubstring.minWindow(source7, target7)); // Expected: "abbbbbcdd"

        String source8 = "baac";
        String target8 = "ac";
        System.out.println("Source: " + source8 + ", Target: " + target8 + ", Result: " + minWindowSubstring.minWindow(source8, target8)); // Expected: "ac" or "ca" (order doesn't matter)

        String source9 = "aa";
        String target9 = "aa";
        System.out.println("Source: " + source9 + ", Target: " + target9 + ", Result: " + minWindowSubstring.minWindow(source9, target9)); // Expected: "aa"

        String source10 = "cabwefgewcwaefgcf";
        String target10 = "cae";
        System.out.println("Source: " + source10 + ", Target: " + target10 + ", Result: " + minWindowSubstring.minWindow(source10, target10)); // Expected: "cwae"
    }
}
