import java.util.*;

public class BusRoutes {

    /**
     * Finds the least number of buses required to travel from a source bus stop to a target bus stop.
     *
     * @param routes A 2D array representing bus routes. routes[i] is a bus route that the ith bus repeats forever.
     * @param source The starting bus stop.
     * @param target The destination bus stop.
     * @return The least number of buses required to travel from source to target, or -1 if it is not possible.
     *
     * Time Complexity: O(R * S), where R is the number of routes and S is the total number of bus stops across all routes.
     * Space Complexity: O(R * S), due to the adjacency list representation.
     */
    public int numBusesToDestination(int[][] routes, int source, int target) {

        if (source == target) {
            return 0; // Already at the target
        }

        // Build an adjacency list: bus stop -> list of buses that visit this stop
        Map<Integer, List<Integer>> stopToBuses = new HashMap<>();
        for (int i = 0; i < routes.length; i++) {
            for (int stop : routes[i]) {
                stopToBuses.computeIfAbsent(stop, k -> new ArrayList<>()).add(i);
            }
        }

        // Queue for BFS: (bus stop, number of buses taken)
        Queue<Integer> queue = new LinkedList<>();
        queue.offer(source);

        // Set to track visited buses (to avoid cycles)
        boolean[] busVisited = new boolean[routes.length];

        // Number of buses taken so far
        int busCount = 0;

        // Set to track visited stops, prevents revisiting the same stops
        Set<Integer> visitedStops = new HashSet<>();
        visitedStops.add(source);

        while (!queue.isEmpty()) {
            int size = queue.size();

            for (int i = 0; i < size; i++) {
                int stop = queue.poll();

                // Iterate through the buses that visit this stop
                for (int bus : stopToBuses.getOrDefault(stop, Collections.emptyList())) {
                    if (!busVisited[bus]) { // Check if bus has been visited
                        busVisited[bus] = true; // Mark bus as visited

                        // Iterate through the stops on this bus route
                        for (int nextStop : routes[bus]) {
                            if (nextStop == target) {
                                return busCount + 1; // Found the target
                            }
                            if (!visitedStops.contains(nextStop)) {
                                queue.offer(nextStop); // Add the next stop to the queue
                                visitedStops.add(nextStop);
                            }

                        }
                    }
                }
            }
            busCount++; // Increment the number of buses
        }

        return -1; // Target is unreachable
    }

    public static void main(String[] args) {
        BusRoutes busRoutes = new BusRoutes();

        // Test case 1
