        int source3 = 7;
        int target3 = 7;
        System.out.println("Test Case 3: " + busRoutes.numBusesToDestination(routes3, source3, target3)); // Expected: 0

        // Test case 4: Target is unreachable
        int[][] routes4 = {{1, 2, 7}, {3, 6, 7}};
        int source4 = 1;
        int target4 = 10;
        System.out.println("Test Case 4: " + busRoutes.numBusesToDestination(routes4, source4, target4)); // Expected: -1

        // Test case 5: Single route
        int[][] routes5 = {{1, 2, 3, 4}};
        int source5 = 1;
        int target5 = 4;
        System.out.println("Test Case 5: " + busRoutes.numBusesToDestination(routes5, source5, target5)); // Expected: 1

        // Test case 6: Empty routes
        int[][] routes6 = {};
        int source6 = 1;
        int target6 = 4;
        System.out.println("Test Case 6: " + busRoutes.numBusesToDestination(routes6, source6, target6)); // Expected: -1

        // Test case 7: Larger dataset
        int[][] routes7 = {{1, 4, 5}, {2, 6}, {7, 8, 9, 10}};
        int source7 = 1;
        int target7 = 10;
        System.out.println("Test Case 7: " + busRoutes.numBusesToDestination(routes7, source7, target7)); // Expected: -1. There is no connection.

        // Test case 8 : Larger dataset with a path.
